import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, FileText, Trash2 } from 'lucide-react';
import api from '../services/api';

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);
    setError(null);

    for (const file of acceptedFiles) {
      console.log('Uploading file:', file.name, file.size, file.type);

      const formData = new FormData();
      formData.append('document', file);
      formData.append('userId', '507f1f77bcf86cd799439011'); // Valid ObjectId format

      try {
        console.log('Making upload request...');
        const response = await api.post('/documents/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        console.log('Upload response:', response.data);
        const result = response.data;
        setDocuments(prev => [...prev, {
          id: result.documentId,
          name: file.name,
          size: file.size,
          processed: result.processed,
          uploadDate: new Date()
        }]);
      } catch (error) {
        console.error('Upload failed:', error);
        const errorMessage = error.response?.data?.error || error.message;
        setError(`Upload failed for ${file.name}: ${errorMessage}`);
      }
    }

    setUploading(false);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="min-h-screen animate-fadeIn" style={{background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 50%, #e2e8f0 100%)'}}>
      <div className="max-w-7xl mx-auto py-12 px-6 lg:px-8">
        <div className="text-center mb-12">
          <div className="w-20 h-20 bg-gradient-to-r from-green-500 to-emerald-600 rounded-3xl flex items-center justify-center mx-auto mb-6">
            <FileText className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-4xl font-bold bg-gradient-to-r from-green-600 to-emerald-600 bg-clip-text text-transparent mb-4">
            Document Management
          </h1>
          <p className="text-xl text-slate-600 max-w-2xl mx-auto">
            Upload and organize your study materials for AI-powered learning
          </p>
        </div>

          {error && (
            <div className="mb-8 p-6 bg-red-50 border border-red-200 text-red-700 rounded-2xl shadow-sm animate-slideIn">
              <div className="flex items-center space-x-3">
                <div className="w-6 h-6 bg-red-500 rounded-full flex items-center justify-center flex-shrink-0">
                  <span className="text-white text-sm font-bold">!</span>
                </div>
                <p className="font-medium">{error}</p>
              </div>
            </div>
          )}

        <div className="mb-12">
          <div 
            {...getRootProps()} 
            className={`card border-2 border-dashed p-12 text-center cursor-pointer transition-all duration-300 group ${
              uploading ? 'opacity-50 pointer-events-none' : ''
            } ${
              isDragActive ? 'border-green-400 bg-green-50/50 scale-105' : 'border-slate-300 hover:border-green-400 hover:bg-green-50/30'
            }`}
          >
            <input {...getInputProps()} />
            <div className="flex flex-col items-center">
              <div className={`w-20 h-20 rounded-3xl flex items-center justify-center mb-6 transition-all duration-300 ${
                isDragActive ? 'bg-green-500 scale-110' : 'bg-slate-100 group-hover:bg-green-100'
              }`}>
                <Upload className={`w-10 h-10 transition-colors duration-300 ${
                  isDragActive ? 'text-white' : 'text-slate-500 group-hover:text-green-600'
                }`} />
              </div>

              {uploading ? (
                <div className="flex flex-col items-center">
                  <div className="w-8 h-8 border-4 border-green-500 border-t-transparent rounded-full animate-spin mb-4"></div>
                  <p className="text-2xl font-bold text-green-600 mb-2">Uploading...</p>
                  <p className="text-slate-600">Processing your documents</p>
                </div>
              ) : isDragActive ? (
                <div>
                  <p className="text-2xl font-bold text-green-600 mb-2">Drop files here!</p>
                  <p className="text-slate-600">Release to upload your documents</p>
                </div>
              ) : (
                <div>
                  <p className="text-2xl font-bold text-slate-800 mb-3">
                    Drag & drop documents here
                  </p>
                  <p className="text-lg text-slate-600 mb-6">
                    or click to browse your files
                  </p>
                  <div className="inline-flex items-center space-x-2 px-6 py-3 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-xl font-medium">
                    <Upload size={20} />
                    <span>Choose Files</span>
                  </div>
                  <p className="text-sm text-slate-500 mt-4">
                    Supported formats: PDF, DOCX, TXT • Max size: 10MB per file
                  </p>
                </div>
              )}
            </div>
          </div>
        </div>

        <div className="card animate-slideIn">
          <div className="p-10">
            <div className="flex items-center justify-between mb-8">
              <div>
                <h3 className="text-2xl font-bold text-slate-800 mb-2">
                  Your Documents
                </h3>
                <p className="text-slate-600">
                  {documents.length} document{documents.length !== 1 ? 's' : ''} uploaded
                </p>
              </div>
              {documents.length > 0 && (
                <div className="text-right">
                  <p className="text-sm text-slate-500">Total storage used</p>
                  <p className="text-lg font-semibold text-slate-800">
                    {formatFileSize(documents.reduce((total, doc) => total + doc.size, 0))}
                  </p>
                </div>
              )}
            </div>

            {documents.length === 0 ? (
              <div className="text-center py-16">
                <div className="w-16 h-16 bg-slate-100 rounded-2xl flex items-center justify-center mx-auto mb-6">
                  <FileText className="w-8 h-8 text-slate-400" />
                </div>
                <h4 className="text-xl font-semibold text-slate-800 mb-3">
                  No documents yet
                </h4>
                <p className="text-slate-600 mb-8 max-w-md mx-auto">
                  Upload your first document to start building your AI-powered knowledge base
                </p>
                <div className="inline-flex items-center space-x-2 px-6 py-3 bg-gradient-to-r from-green-500 to-emerald-600 text-white rounded-xl font-medium cursor-pointer"
                     {...getRootProps()}>
                  <input {...getInputProps()} />
                  <Upload size={20} />
                  <span>Upload First Document</span>
                </div>
              </div>
            ) : (
              <div className="grid gap-4">
                {documents.map((doc, index) => (
                  <div key={doc.id} className="flex items-center justify-between p-6 bg-white/60 backdrop-blur-sm border border-white/20 rounded-2xl hover:bg-white/80 transition-all duration-200 group animate-slideIn" style={{animationDelay: `${index * 0.1}s`}}>
                    <div className="flex items-center space-x-6">
                      <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center">
                        <FileText className="w-6 h-6 text-white" />
                      </div>
                      <div>
                        <p className="font-semibold text-slate-800 text-lg mb-1">{doc.name}</p>
                        <div className="flex items-center space-x-4 text-sm">
                          <span className="text-slate-600">{formatFileSize(doc.size)}</span>
                          <span className="text-slate-400">•</span>
                          <div className={`flex items-center space-x-2 px-3 py-1 rounded-full text-xs font-medium ${
                            doc.processed 
                              ? 'bg-green-100 text-green-700' 
                              : 'bg-orange-100 text-orange-700'
                          }`}>
                            <div className={`w-2 h-2 rounded-full ${
                              doc.processed ? 'bg-green-500' : 'bg-orange-500 animate-pulse'
                            }`}></div>
                            <span>{doc.processed ? 'Ready' : 'Processing'}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                    <button className="p-3 text-slate-400 hover:text-red-500 hover:bg-red-50 rounded-xl transition-all duration-200 opacity-0 group-hover:opacity-100">
                      <Trash2 className="w-5 h-5" />
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Documents;
