import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [activeModal, setActiveModal] = useState(null);
  const [formData, setFormData] = useState({});
  const [file, setFile] = useState(null);
  const [questions, setQuestions] = useState([{ question: '', options: ['', '', '', ''], correctAnswer: 0 }]);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('adminData');
    navigate('/admin');
  };

  const handleSubmit = async (endpoint, data) => {
    const token = localStorage.getItem('adminToken');
    const formDataObj = new FormData();

    Object.keys(data).forEach(key => {
      if (key === 'questions') {
        formDataObj.append(key, JSON.stringify(data[key]));
      } else {
        formDataObj.append(key, data[key]);
      }
    });

    if (file) formDataObj.append(endpoint.includes('timetable') ? 'image' : 'file', file);

    try {
      const response = await fetch(`http://localhost:5002/api/admin/${endpoint}`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` },
        body: formDataObj
      });

      if (response.ok) {
        alert('Success!');
        setActiveModal(null);
        setFormData({});
        setFile(null);
      } else {
        alert('Error occurred');
      }
    } catch (error) {
      alert('Error occurred');
    }
  };

  const addQuestion = () => {
    setQuestions([...questions, { question: '', options: ['', '', '', ''], correctAnswer: 0 }]);
  };

  const updateQuestion = (index, field, value) => {
    const updated = [...questions];
    if (field === 'options') {
      updated[index].options = value;
    } else {
      updated[index][field] = value;
    }
    setQuestions(updated);
  };

  const cards = [
    {
      title: 'Upload Notes',
      description: 'Upload study materials and notes',
      action: () => setActiveModal('notes')
    },
    {
      title: 'Timetable',
      description: 'Create or upload timetable images',
      action: () => setActiveModal('timetable')
    },
    {
      title: 'Important Notes',
      description: 'Send important notifications to students',
      action: () => setActiveModal('important')
    },
    {
      title: 'MCQ Tests',
      description: 'Create and manage MCQ tests',
      action: () => setActiveModal('mcq')
    }
  ];

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Admin Dashboard</h1>
          <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded-lg">
            Logout
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {cards.map((card, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-md cursor-pointer hover:shadow-lg transition-shadow" onClick={card.action}>
              <h3 className="text-xl font-semibold mb-2">{card.title}</h3>
              <p className="text-gray-600">{card.description}</p>
            </div>
          ))}
        </div>

        {/* Notes Modal */}
        {activeModal === 'notes' && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-6 rounded-lg w-96">
              <h3 className="text-xl font-bold mb-4">Upload Notes</h3>
              <input
                type="text"
                placeholder="Title"
                className="w-full p-2 border rounded mb-3"
                onChange={(e) => setFormData({...formData, title: e.target.value})}
              />
              <textarea
                placeholder="Content"
                className="w-full p-2 border rounded mb-3 h-24"
                onChange={(e) => setFormData({...formData, content: e.target.value})}
              />
              <input
                type="file"
                className="w-full p-2 border rounded mb-4"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <div className="flex gap-2">
                <button onClick={() => handleSubmit('notes', formData)} className="bg-blue-600 text-white px-4 py-2 rounded">
                  Upload
                </button>
                <button onClick={() => setActiveModal(null)} className="bg-gray-600 text-white px-4 py-2 rounded">
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Timetable Modal */}
        {activeModal === 'timetable' && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-6 rounded-lg w-96">
              <h3 className="text-xl font-bold mb-4">Upload Timetable</h3>
              <input
                type="text"
                placeholder="Title"
                className="w-full p-2 border rounded mb-3"
                onChange={(e) => setFormData({...formData, title: e.target.value})}
              />
              <textarea
                placeholder="Description"
                className="w-full p-2 border rounded mb-3 h-20"
                onChange={(e) => setFormData({...formData, description: e.target.value})}
              />
              <input
                type="file"
                accept="image/*"
                className="w-full p-2 border rounded mb-4"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <div className="flex gap-2">
                <button onClick={() => handleSubmit('timetable', formData)} className="bg-blue-600 text-white px-4 py-2 rounded">
                  Upload
                </button>
                <button onClick={() => setActiveModal(null)} className="bg-gray-600 text-white px-4 py-2 rounded">
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Important Notes Modal */}
        {activeModal === 'important' && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-6 rounded-lg w-96">
              <h3 className="text-xl font-bold mb-4">Send Important Note</h3>
              <input
                type="text"
                placeholder="Title"
                className="w-full p-2 border rounded mb-3"
                onChange={(e) => setFormData({...formData, title: e.target.value})}
              />
              <textarea
                placeholder="Message"
                className="w-full p-2 border rounded mb-3 h-24"
                onChange={(e) => setFormData({...formData, message: e.target.value})}
              />
              <select
                className="w-full p-2 border rounded mb-4"
                onChange={(e) => setFormData({...formData, priority: e.target.value})}
              >
                <option value="medium">Medium Priority</option>
                <option value="low">Low Priority</option>
                <option value="high">High Priority</option>
              </select>
              <div className="flex gap-2">
                <button onClick={() => handleSubmit('important-notes', formData)} className="bg-blue-600 text-white px-4 py-2 rounded">
                  Send
                </button>
                <button onClick={() => setActiveModal(null)} className="bg-gray-600 text-white px-4 py-2 rounded">
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* MCQ Modal */}
        {activeModal === 'mcq' && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center overflow-y-auto">
            <div className="bg-white p-6 rounded-lg w-[600px] max-h-[90vh] overflow-y-auto">
              <h3 className="text-xl font-bold mb-4">Create MCQ Test</h3>
              <input
                type="text"
                placeholder="Test Title"
                className="w-full p-2 border rounded mb-3"
                onChange={(e) => setFormData({...formData, title: e.target.value})}
              />
              <textarea
                placeholder="Description"
                className="w-full p-2 border rounded mb-3 h-20"
                onChange={(e) => setFormData({...formData, description: e.target.value})}
              />
              <input
                type="number"
                placeholder="Time Limit (minutes)"
                className="w-full p-2 border rounded mb-4"
                onChange={(e) => setFormData({...formData, timeLimit: e.target.value})}
              />

              {questions.map((q, qIndex) => (
                <div key={qIndex} className="border p-4 rounded mb-4">
                  <input
                    type="text"
                    placeholder={`Question ${qIndex + 1}`}
                    className="w-full p-2 border rounded mb-2"
                    value={q.question}
                    onChange={(e) => updateQuestion(qIndex, 'question', e.target.value)}
                  />
                  {q.options.map((option, oIndex) => (
                    <div key={oIndex} className="flex items-center mb-2">
                      <input
                        type="radio"
                        name={`correct-${qIndex}`}
                        checked={q.correctAnswer === oIndex}
                        onChange={() => updateQuestion(qIndex, 'correctAnswer', oIndex)}
                        className="mr-2"
                      />
                      <input
                        type="text"
                        placeholder={`Option ${oIndex + 1}`}
                        className="flex-1 p-2 border rounded"
                        value={option}
                        onChange={(e) => {
                          const newOptions = [...q.options];
                          newOptions[oIndex] = e.target.value;
                          updateQuestion(qIndex, 'options', newOptions);
                        }}
                      />
                    </div>
                  ))}
                </div>
              ))}

              <button onClick={addQuestion} className="bg-green-600 text-white px-4 py-2 rounded mb-4">
                Add Question
              </button>

              <div className="flex gap-2">
                <button onClick={() => handleSubmit('mcq-tests', {...formData, questions})} className="bg-blue-600 text-white px-4 py-2 rounded">
                  Create Test
                </button>
                <button onClick={() => setActiveModal(null)} className="bg-gray-600 text-white px-4 py-2 rounded">
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
