import React, { useState, useEffect } from 'react';
import { Send, Mic, MicOff, FileText, Brain, Calendar } from 'lucide-react';
import api from '../services/api';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isListening, setIsListening] = useState(false);
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [messageType, setMessageType] = useState('chat');
  const [quiz, setQuiz] = useState(null);
  const [studyPlan, setStudyPlan] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadDocuments();
    createSession();
  }, []);

  const loadDocuments = async () => {
    try {
      const response = await api.get('/documents/507f1f77bcf86cd799439011');
      setDocuments(response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
    }
  };

  const createSession = async () => {
    try {
      const response = await api.post('/chat/session', {
        userId: '507f1f77bcf86cd799439011'
      });
      setSessionId(response.data.sessionId);
    } catch (error) {
      console.error('Failed to create session:', error);
    }
  };

  const handleSend = async () => {
    if (!message.trim() && messageType === 'chat') return;
    if (!sessionId) return;

    setLoading(true);
    const userMessage = {
      role: 'user',
      content: message || (messageType === 'quiz' ? 'Generate a quiz' : 'Create a study plan'),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await api.post('/chat/message', {
        sessionId,
        message: message || (messageType === 'quiz' ? 'Generate a quiz' : 'Create a study plan'),
        documentIds: selectedDocs,
        userId: '507f1f77bcf86cd799439011',
        messageType
      });

      // Add AI response
      setMessages(prev => [...prev, response.data.message]);

      // Handle different response types
      if (response.data.quiz) {
        setQuiz(response.data.quiz);
      }
      if (response.data.studyPlan) {
        setStudyPlan(response.data.studyPlan);
      }

      setMessage('');
      setMessageType('chat');
    } catch (error) {
      console.error('Failed to send message:', error);
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date()
      }]);
    } finally {
      setLoading(false);
    }
  };

  const toggleDocumentSelection = (docId) => {
    setSelectedDocs(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const renderQuiz = () => {
    if (!quiz) return null;

    return (
      <div className="bg-blue-50 p-4 rounded-lg mb-4">
        <h3 className="font-bold mb-3">üìù Generated Quiz</h3>
        {quiz.map((q, index) => (
          <div key={index} className="mb-4 p-3 bg-white rounded border">
            <p className="font-medium mb-2">{index + 1}. {q.question}</p>
            <div className="space-y-1">
              {q.options.map((option, i) => (
                <div key={i} className={`p-2 rounded ${i === q.correct ? 'bg-green-100' : 'bg-gray-50'}`}>
                  {String.fromCharCode(65 + i)}. {option}
                </div>
              ))}
            </div>
            <p className="text-sm text-gray-600 mt-2">üí° {q.explanation}</p>
          </div>
        ))}
      </div>
    );
  };

  const renderStudyPlan = () => {
    if (!studyPlan) return null;

    return (
      <div className="bg-green-50 p-4 rounded-lg mb-4">
        <h3 className="font-bold mb-3">üìÖ {studyPlan.title}</h3>
        <p className="text-sm text-gray-600 mb-3">Duration: {studyPlan.totalDuration}</p>
        {studyPlan.schedule.slice(0, 3).map((day, index) => (
          <div key={index} className="mb-3 p-3 bg-white rounded border">
            <h4 className="font-medium">Day {day.day} - {day.date}</h4>
            {day.sessions.map((session, i) => (
              <div key={i} className="mt-2">
                <p className="text-sm font-medium">{session.time}: {session.topic}</p>
                <ul className="text-sm text-gray-600 ml-4">
                  {session.activities.map((activity, j) => (
                    <li key={j}>‚Ä¢ {activity}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="h-screen bg-gray-100">
      <div className="flex h-full">
        <div className="w-80 bg-white border-r border-gray-200 p-4 overflow-y-auto">
          <h2 className="text-xl font-bold mb-4">AI Assistant</h2>
          <div className="text-sm mb-4 p-2 bg-gray-50 rounded">
            Status: {sessionId ? 'üü¢ Connected' : 'üî¥ Connecting...'}
          </div>

          <div className="mb-4">
            <h3 className="font-medium mb-2">üìö Your Documents ({documents.length})</h3>
            {documents.length === 0 ? (
              <div className="text-sm text-gray-500 p-3 bg-gray-50 rounded">
                No documents uploaded yet. 
                <br />Upload documents first to ask questions about them.
              </div>
            ) : (
              <div className="space-y-2 max-h-40 overflow-y-auto">
                {documents.map((doc) => (
                  <div key={doc._id} className={`flex items-center space-x-2 p-2 rounded ${
                    selectedDocs.includes(doc._id) ? 'bg-blue-50 border border-blue-200' : 'hover:bg-gray-50'
                  }`}>
                    <input
                      type="checkbox"
                      checked={selectedDocs.includes(doc._id)}
                      onChange={() => toggleDocumentSelection(doc._id)}
                      className="rounded"
                    />
                    <div className="flex-1 min-w-0">
                      <span className="text-sm truncate block">{doc.fileName}</span>
                      <span className="text-xs text-gray-500">
                        {doc.processed ? '‚úÖ Ready' : '‚è≥ Processing'}
                      </span>
                    </div>
                  </div>
                ))}
              </div>
            )}
            {selectedDocs.length > 0 && (
              <div className="mt-2 text-xs text-blue-600">
                {selectedDocs.length} document(s) selected for chat
              </div>
            )}
          </div>

          <div className="mb-4">
            <h3 className="font-medium mb-2">üéØ Quick Actions</h3>
            <div className="space-y-2">
              <button
                onClick={() => setMessageType('quiz')}
                className={`w-full p-2 text-left rounded text-sm ${
                  messageType === 'quiz' ? 'bg-blue-100' : 'bg-gray-100'
                }`}
              >
                <Brain className="inline w-4 h-4 mr-2" />
                Generate Quiz
              </button>
              <button
                onClick={() => setMessageType('study-plan')}
                className={`w-full p-2 text-left rounded text-sm ${
                  messageType === 'study-plan' ? 'bg-green-100' : 'bg-gray-100'
                }`}
              >
                <Calendar className="inline w-4 h-4 mr-2" />
                Create Study Plan
              </button>
            </div>
          </div>
        </div>

        <div className="flex-1 flex flex-col">
          <div className="flex-1 overflow-y-auto p-4 bg-gray-50">
            {renderQuiz()}
            {renderStudyPlan()}
            {messages.length === 0 && (
              <div className="text-center text-gray-500 py-8">
                <h3 className="text-lg font-medium mb-2">Welcome to your AI Study Assistant!</h3>
                <p className="mb-4">I can help you with:</p>
                <ul className="text-sm space-y-1 mb-4">
                  <li>üìö Answering questions about your uploaded documents</li>
                  <li>üß† Generating quizzes from your study materials</li>
                  <li>üìÖ Creating personalized study plans</li>
                  <li>üí¨ General academic assistance</li>
                </ul>
                <p className="text-xs text-gray-400">
                  {documents.length === 0 
                    ? 'Upload documents first, then start asking questions!' 
                    : 'Select documents above and start asking questions!'}
                </p>
              </div>
            )}
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`mb-4 ${
                  msg.role === 'user' 
                    ? 'ml-8 text-right' 
                    : 'mr-8 text-left'
                }`}
              >
                <div className={`inline-block p-3 rounded-lg max-w-full ${
                  msg.role === 'user'
                    ? 'bg-blue-500 text-white'
                    : 'bg-gray-100 text-gray-900'
                }`}>
                  <div className="whitespace-pre-wrap">{msg.content}</div>
                  {msg.sources && msg.sources.length > 0 && (
                    <div className="mt-2 text-xs opacity-75 border-t pt-2">
                      üìÑ Sources: {msg.sources.length} document section{msg.sources.length > 1 ? 's' : ''}
                    </div>
                  )}
                  <div className="text-xs opacity-50 mt-1">
                    {new Date(msg.timestamp).toLocaleTimeString()}
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="border-t bg-white p-4">
            <div className="flex gap-2 max-w-4xl mx-auto">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={
                  messageType === 'chat' 
                    ? (selectedDocs.length > 0 
                        ? `Ask questions about your selected document${selectedDocs.length > 1 ? 's' : ''}...` 
                        : 'Ask me anything or select documents above for specific questions...')
                    : messageType === 'quiz' 
                      ? 'Click send to generate quiz from selected documents' 
                      : 'Click send to create study plan from selected documents'
                }
                className="flex-1 p-3 border rounded-lg resize-none"
                rows="2"
                disabled={messageType !== 'chat'}
              />
              <button
                onClick={() => setIsListening(!isListening)}
                className={`p-3 rounded-lg ${
                  isListening ? 'bg-red-500 text-white' : 'bg-gray-200'
                }`}
              >
                {isListening ? <MicOff size={20} /> : <Mic size={20} />}
              </button>
              <button
                onClick={handleSend}
                disabled={(messageType === 'chat' && !message.trim()) || loading || (messageType !== 'chat' && selectedDocs.length === 0)}
                className={`p-3 text-white rounded-lg disabled:opacity-50 disabled:cursor-not-allowed ${
                  messageType === 'quiz' ? 'bg-purple-500 hover:bg-purple-600' :
                  messageType === 'study-plan' ? 'bg-green-500 hover:bg-green-600' : 'bg-blue-500 hover:bg-blue-600'
                }`}
                title={
                  messageType !== 'chat' && selectedDocs.length === 0 
                    ? 'Please select documents first' 
                    : messageType === 'chat' && !message.trim() 
                      ? 'Type a message first' 
                      : 'Send message'
                }
              >
                {loading ? '‚è≥' :
                 messageType === 'quiz' ? <Brain size={20} /> :
                 messageType === 'study-plan' ? <Calendar size={20} /> :
                 <Send size={20} />}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
