import React, { useState, useEffect, useCallback } from 'react';
import { Send, Mic, MicOff, FileText, Brain, Calendar, Upload } from 'lucide-react';
import { useDropzone } from 'react-dropzone';
import api from '../services/api';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isListening, setIsListening] = useState(false);
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [messageType, setMessageType] = useState('chat');
  const [quiz, setQuiz] = useState(null);
  const [studyPlan, setStudyPlan] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    loadDocuments();
    createSession();
  }, []);

  const loadDocuments = async () => {
    try {
      const response = await api.get('/documents/507f1f77bcf86cd799439011');
      setDocuments(response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
    }
  };

  const createSession = async () => {
    try {
      const response = await api.post('/chat/session', {
        userId: '507f1f77bcf86cd799439011'
      });
      setSessionId(response.data.sessionId);
    } catch (error) {
      console.error('Failed to create session:', error);
    }
  };

  const handleSend = async () => {
    if (!message.trim() && messageType === 'chat') return;
    if (!sessionId) return;

    setLoading(true);
    const userMessage = {
      role: 'user',
      content: message || (messageType === 'quiz' ? 'Generate a quiz' : 'Create a study plan'),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await api.post('/chat/message', {
        sessionId,
        message: message || (messageType === 'quiz' ? 'Generate a quiz' : 'Create a study plan'),
        documentIds: selectedDocs,
        userId: '507f1f77bcf86cd799439011',
        messageType
      });

      // Add AI response
      setMessages(prev => [...prev, response.data.message]);

      // Handle different response types
      if (response.data.quiz) {
        setQuiz(response.data.quiz);
      }
      if (response.data.studyPlan) {
        setStudyPlan(response.data.studyPlan);
      }

      setMessage('');
      setMessageType('chat');
    } catch (error) {
      console.error('Failed to send message:', error);
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date()
      }]);
    } finally {
      setLoading(false);
    }
  };

  const toggleDocumentSelection = (docId) => {
    setSelectedDocs(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);

    for (const file of acceptedFiles) {
      const formData = new FormData();
      formData.append('document', file);
      formData.append('userId', '507f1f77bcf86cd799439011');

      try {
        const response = await api.post('/documents/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });

        const newDoc = {
          _id: response.data.documentId,
          fileName: file.name,
          processed: response.data.processed
        };

        setDocuments(prev => [...prev, newDoc]);
        setSelectedDocs(prev => [...prev, newDoc._id]);
      } catch (error) {
        console.error('Upload failed:', error);
      }
    }

    setUploading(false);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'application/vnd.ms-powerpoint': ['.ppt'],
      'application/vnd.openxmlformats-officedocument.presentationml.presentation': ['.pptx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const renderQuiz = () => {
    if (!quiz) return null;

    return (
      <div className="bg-blue-50 p-4 rounded-lg mb-4">
        <h3 className="font-bold mb-3">üìù Generated Quiz</h3>
        {quiz.map((q, index) => (
          <div key={index} className="mb-4 p-3 bg-white rounded border">
            <p className="font-medium mb-2">{index + 1}. {q.question}</p>
            <div className="space-y-1">
              {q.options.map((option, i) => (
                <div key={i} className={`p-2 rounded ${i === q.correct ? 'bg-green-100' : 'bg-gray-50'}`}>
                  {String.fromCharCode(65 + i)}. {option}
                </div>
              ))}
            </div>
            <p className="text-sm text-gray-600 mt-2">üí° {q.explanation}</p>
          </div>
        ))}
      </div>
    );
  };

  const renderStudyPlan = () => {
    if (!studyPlan) return null;

    return (
      <div className="bg-green-50 p-4 rounded-lg mb-4">
        <h3 className="font-bold mb-3">üìÖ {studyPlan.title}</h3>
        <p className="text-sm text-gray-600 mb-3">Duration: {studyPlan.totalDuration}</p>
        {studyPlan.schedule.slice(0, 3).map((day, index) => (
          <div key={index} className="mb-3 p-3 bg-white rounded border">
            <h4 className="font-medium">Day {day.day} - {day.date}</h4>
            {day.sessions.map((session, i) => (
              <div key={i} className="mt-2">
                <p className="text-sm font-medium">{session.time}: {session.topic}</p>
                <ul className="text-sm text-gray-600 ml-4">
                  {session.activities.map((activity, j) => (
                    <li key={j}>‚Ä¢ {activity}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ))}
      </div>
    );
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="chat-container">
      <div className="flex h-screen">
        <div className="sidebar">
          <h2 className="text-xl font-bold mb-4">AI Assistant</h2>
          <div className="text-sm mb-4">
            Status: {sessionId ? 'üü¢ Connected' : 'üî¥ Connecting...'}
          </div>

          <div className="mb-6">
            <h3 className="font-semibold mb-3 text-gray-800">üìö Upload & Chat</h3>
            <div 
              {...getRootProps()} 
              className={`border-2 border-dashed rounded-xl p-4 text-center cursor-pointer mb-4 transition-all ${
                isDragActive ? 'bg-blue-50' : 'hover:bg-gray-50'
              } ${uploading ? 'opacity-50' : ''}`}
              style={{borderColor: isDragActive ? '#2196f3' : '#e3f2fd', background: isDragActive ? '#e3f2fd' : 'rgba(255, 255, 255, 0.5)'}}
            >
              <input {...getInputProps()} />
              <Upload className="mx-auto h-6 w-6 text-gray-400 mb-1" />
              {uploading ? (
                <p className="text-xs text-gray-600">Uploading...</p>
              ) : (
                <div>
                  <p className="text-xs text-gray-600">Drop file or click</p>
                  <p className="text-xs text-gray-400">PDF, DOCX, PPT, TXT</p>
                </div>
              )}
            </div>

            {documents.length > 0 && (
              <div className="space-y-2 max-h-32 overflow-y-auto">
                {documents.map((doc) => (
                  <div key={doc._id} className={`flex items-center space-x-3 p-2 rounded-lg text-sm transition-all ${
                    selectedDocs.includes(doc._id) ? 'bg-blue-50 border border-blue-200' : 'hover:bg-gray-50'
                  }`}>
                    <input
                      type="checkbox"
                      checked={selectedDocs.includes(doc._id)}
                      onChange={() => toggleDocumentSelection(doc._id)}
                      className="rounded"
                      style={{accentColor: '#2196f3'}}
                    />
                    <span className="truncate text-gray-700">{doc.fileName}</span>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="mb-6">
            <h3 className="font-semibold mb-3 text-gray-800">üéØ Quick Actions</h3>
            <div className="space-y-3">
              <button
                onClick={() => {
                  setMessageType('quiz');
                  setMessage('');
                }}
                className={`w-full p-3 text-left rounded-xl text-sm font-medium transition-all ${
                  messageType === 'quiz' ? 'bg-blue-50 border border-blue-200 text-blue-700' : 'bg-white hover:bg-gray-50 border border-gray-200'
                }`}
              >
                <Brain className="inline w-5 h-5 mr-3" style={{color: messageType === 'quiz' ? '#2196f3' : '#9c27b0'}} />
                Generate Quiz
              </button>
              <button
                onClick={() => {
                  setMessageType('study-plan');
                  setMessage('');
                }}
                className={`w-full p-3 text-left rounded-xl text-sm font-medium transition-all ${
                  messageType === 'study-plan' ? 'bg-green-50 border border-green-200 text-green-700' : 'bg-white hover:bg-gray-50 border border-gray-200'
                }`}
              >
                <Calendar className="inline w-5 h-5 mr-3" style={{color: messageType === 'study-plan' ? '#4caf50' : '#ff9800'}} />
                Create Study Plan
              </button>
            </div>
          </div>
        </div>

        <div className="main-content flex flex-col">
          <div className="messages-container">
            {renderQuiz()}
            {renderStudyPlan()}
            {messages.map((msg, index) => (
              <div
                key={index}
                className={`message-bubble ${
                  msg.role === 'user' ? 'message-user' : 'message-assistant'
                }`}
              >
                {msg.content}
                {msg.sources && msg.sources.length > 0 && (
                  <div className="mt-2 text-xs opacity-75">
                    üìÑ Sources: {msg.sources.length} document chunks
                  </div>
                )}
              </div>
            ))}
          </div>

          <div className="input-container">
            <div className="flex gap-3">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                onKeyPress={handleKeyPress}
                placeholder={messageType === 'chat' ? 'Ask me anything...' : 
                           messageType === 'quiz' ? 'Click send to generate quiz from selected documents' :
                           'Click send to create study plan from selected documents'}
                className="flex-1 p-4 border rounded-xl resize-none transition-all"
                style={{borderColor: '#e3f2fd', background: 'rgba(255, 255, 255, 0.9)'}}
                onFocus={(e) => e.target.style.borderColor = '#2196f3'}
                onBlur={(e) => e.target.style.borderColor = '#e3f2fd'}
                rows="2"
                disabled={messageType !== 'chat'}
              />
              <button
                onClick={() => setIsListening(!isListening)}
                className={`p-4 rounded-xl transition-all ${
                  isListening ? 'text-white' : 'text-gray-600 hover:text-gray-800'
                }`}
                style={{background: isListening ? 'linear-gradient(135deg, #f44336 0%, #d32f2f 100%)' : 'rgba(255, 255, 255, 0.9)', border: '1px solid #e3f2fd'}}
              >
                {isListening ? <MicOff size={20} /> : <Mic size={20} />}
              </button>
              <button
                onClick={handleSend}
                disabled={(messageType === 'chat' && !message.trim()) || loading}
                className="p-4 text-white rounded-xl disabled:opacity-50 transition-all transform hover:scale-105"
                style={{
                  background: messageType === 'quiz' ? 'linear-gradient(135deg, #9c27b0 0%, #7b1fa2 100%)' :
                            messageType === 'study-plan' ? 'linear-gradient(135deg, #4caf50 0%, #388e3c 100%)' : 
                            'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)',
                  boxShadow: '0 4px 12px rgba(33, 150, 243, 0.3)'
                }}
              >
                {loading ? '...' :
                 messageType === 'quiz' ? <Brain size={20} /> :
                 messageType === 'study-plan' ? <Calendar size={20} /> :
                 <Send size={20} />}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
