import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [activeModal, setActiveModal] = useState(null);
  const [formData, setFormData] = useState({});
  const [file, setFile] = useState(null);
  const [questions, setQuestions] = useState([{ question: '', options: ['', '', '', ''], correctAnswer: 0 }]);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('adminData');
    navigate('/admin');
  };

  const handleSubmit = async (endpoint, data) => {
    const token = localStorage.getItem('adminToken');
    const formDataObj = new FormData();

    Object.keys(data).forEach(key => {
      if (key === 'questions') {
        formDataObj.append(key, JSON.stringify(data[key]));
      } else {
        formDataObj.append(key, data[key]);
      }
    });

    if (file) formDataObj.append(endpoint.includes('timetable') ? 'image' : 'file', file);

    try {
      const response = await fetch(`http://localhost:5002/api/admin/${endpoint}`, {
        method: 'POST',
        headers: { 'Authorization': `Bearer ${token}` },
        body: formDataObj
      });

      if (response.ok) {
        alert('Success!');
        setActiveModal(null);
        setFormData({});
        setFile(null);
      } else {
        alert('Error occurred');
      }
    } catch (error) {
      alert('Error occurred');
    }
  };

  const addQuestion = () => {
    setQuestions([...questions, { question: '', options: ['', '', '', ''], correctAnswer: 0 }]);
  };

  const updateQuestion = (index, field, value) => {
    const updated = [...questions];
    if (field === 'options') {
      updated[index].options = value;
    } else {
      updated[index][field] = value;
    }
    setQuestions(updated);
  };

  const cards = [
    {
      title: 'Upload Notes',
      description: 'Upload study materials and notes',
      action: () => setActiveModal('notes')
    },
    {
      title: 'Timetable',
      description: 'Create or upload timetable images',
      action: () => setActiveModal('timetable')
    },
    {
      title: 'Important Notes',
      description: 'Send important notifications to students',
      action: () => setActiveModal('important')
    },
    {
      title: 'MCQ Tests',
      description: 'Create and manage MCQ tests',
      action: () => setActiveModal('mcq')
    }
  ];

  return (
    <div className="admin-dashboard-page">
      <div className="admin-dashboard-container">
        <div className="admin-dashboard-header">
          <div className="admin-dashboard-title">
            <h1>Admin Dashboard</h1>
            <p>Manage resources, announcements, and assessments</p>
          </div>
          <button onClick={handleLogout} className="admin-dashboard-logout">
            Logout
          </button>
        </div>

        <div className="admin-dashboard-grid">
          {cards.map((card, index) => (
            <div key={index} className="admin-dashboard-card" onClick={card.action}>
              <h3>{card.title}</h3>
              <p>{card.description}</p>
            </div>
          ))}
        </div>

        {/* Notes Modal */}
        {activeModal === 'notes' && (
          <div className="admin-dashboard-modal-overlay">
            <div className="admin-dashboard-modal">
              <h3>Upload Notes</h3>
              <div className="admin-dashboard-field">
                <input
                  type="text"
                  placeholder="Title"
                  className="admin-dashboard-input"
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <textarea
                  placeholder="Content"
                  className="admin-dashboard-textarea"
                  onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <input
                  type="file"
                  className="admin-dashboard-file"
                  onChange={(e) => setFile(e.target.files[0])}
                />
              </div>
              <div className="admin-dashboard-modal-actions">
                <button
                  onClick={() => handleSubmit('notes', formData)}
                  className="admin-dashboard-btn admin-dashboard-btn--primary"
                >
                  Upload
                </button>
                <button
                  onClick={() => setActiveModal(null)}
                  className="admin-dashboard-btn admin-dashboard-btn--secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Timetable Modal */}
        {activeModal === 'timetable' && (
          <div className="admin-dashboard-modal-overlay">
            <div className="admin-dashboard-modal">
              <h3>Upload Timetable</h3>
              <div className="admin-dashboard-field">
                <input
                  type="text"
                  placeholder="Title"
                  className="admin-dashboard-input"
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <textarea
                  placeholder="Description"
                  className="admin-dashboard-textarea"
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <input
                  type="file"
                  accept="image/*"
                  className="admin-dashboard-file"
                  onChange={(e) => setFile(e.target.files[0])}
                />
              </div>
              <div className="admin-dashboard-modal-actions">
                <button
                  onClick={() => handleSubmit('timetable', formData)}
                  className="admin-dashboard-btn admin-dashboard-btn--primary"
                >
                  Upload
                </button>
                <button
                  onClick={() => setActiveModal(null)}
                  className="admin-dashboard-btn admin-dashboard-btn--secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Important Notes Modal */}
        {activeModal === 'important' && (
          <div className="admin-dashboard-modal-overlay">
            <div className="admin-dashboard-modal">
              <h3>Send Important Note</h3>
              <div className="admin-dashboard-field">
                <input
                  type="text"
                  placeholder="Title"
                  className="admin-dashboard-input"
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <textarea
                  placeholder="Message"
                  className="admin-dashboard-textarea"
                  onChange={(e) => setFormData({ ...formData, message: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <select
                  className="admin-dashboard-select"
                  onChange={(e) => setFormData({ ...formData, priority: e.target.value })}
                  defaultValue="medium"
                >
                  <option value="medium">Medium Priority</option>
                  <option value="low">Low Priority</option>
                  <option value="high">High Priority</option>
                </select>
              </div>
              <div className="admin-dashboard-modal-actions">
                <button
                  onClick={() => handleSubmit('important-notes', formData)}
                  className="admin-dashboard-btn admin-dashboard-btn--primary"
                >
                  Send
                </button>
                <button
                  onClick={() => setActiveModal(null)}
                  className="admin-dashboard-btn admin-dashboard-btn--secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}

        {/* MCQ Modal */}
        {activeModal === 'mcq' && (
          <div className="admin-dashboard-modal-overlay">
            <div className="admin-dashboard-modal admin-dashboard-modal--wide">
              <h3>Create MCQ Test</h3>
              <div className="admin-dashboard-field">
                <input
                  type="text"
                  placeholder="Test Title"
                  className="admin-dashboard-input"
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <textarea
                  placeholder="Description"
                  className="admin-dashboard-textarea"
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                />
              </div>
              <div className="admin-dashboard-field">
                <input
                  type="number"
                  placeholder="Time Limit (minutes)"
                  className="admin-dashboard-input"
                  onChange={(e) => setFormData({ ...formData, timeLimit: e.target.value })}
                />
              </div>

              <div className="admin-dashboard-question-list">
                {questions.map((q, qIndex) => (
                  <div key={qIndex} className="admin-dashboard-question">
                    <input
                      type="text"
                      placeholder={`Question ${qIndex + 1}`}
                      className="admin-dashboard-input"
                      value={q.question}
                      onChange={(e) => updateQuestion(qIndex, 'question', e.target.value)}
                    />
                    {q.options.map((option, oIndex) => (
                      <div key={oIndex} className="admin-dashboard-option">
                        <input
                          type="radio"
                          name={`correct-${qIndex}`}
                          checked={q.correctAnswer === oIndex}
                          onChange={() => updateQuestion(qIndex, 'correctAnswer', oIndex)}
                        />
                        <input
                          type="text"
                          placeholder={`Option ${oIndex + 1}`}
                          className="admin-dashboard-input"
                          value={option}
                          onChange={(e) => {
                            const newOptions = [...q.options];
                            newOptions[oIndex] = e.target.value;
                            updateQuestion(qIndex, 'options', newOptions);
                          }}
                        />
                      </div>
                    ))}
                  </div>
                ))}
              </div>

              <button onClick={addQuestion} className="admin-dashboard-add-question">
                Add Question
              </button>

              <div className="admin-dashboard-modal-actions">
                <button
                  onClick={() => handleSubmit('mcq-tests', { ...formData, questions })}
                  className="admin-dashboard-btn admin-dashboard-btn--primary"
                >
                  Create Test
                </button>
                <button
                  onClick={() => setActiveModal(null)}
                  className="admin-dashboard-btn admin-dashboard-btn--secondary"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
