import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Send, Mic, MicOff, FileText, Brain, Calendar, Upload, MessageCircle, Sparkles, BookOpen } from 'lucide-react';
import { useDropzone } from 'react-dropzone';
import api from '../services/api';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [isListening, setIsListening] = useState(false);
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [messageType, setMessageType] = useState('chat');
  const [quiz, setQuiz] = useState(null);
  const [studyPlan, setStudyPlan] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    loadDocuments();
    createSession();
  }, []);

  const loadDocuments = async () => {
    try {
      const response = await api.get('/documents/507f1f77bcf86cd799439011');
      setDocuments(response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
    }
  };

  const createSession = async () => {
    try {
      const response = await api.post('/chat/session', {
        userId: '507f1f77bcf86cd799439011'
      });
      setSessionId(response.data.sessionId);
    } catch (error) {
      console.error('Failed to create session:', error);
    }
  };

  const handleSend = async () => {
    if (!message.trim() && messageType === 'chat') return;
    if (!sessionId) return;

    setLoading(true);
    const userMessage = {
      role: 'user',
      content: message || (messageType === 'quiz' ? 'Generate a quiz' : 'Create a study plan'),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await api.post('/chat/message', {
        sessionId,
        message: message || (messageType === 'quiz' ? 'Generate a quiz' : 'Create a study plan'),
        documentIds: selectedDocs,
        userId: '507f1f77bcf86cd799439011',
        messageType
      });

      // Add AI response
      setMessages(prev => [...prev, response.data.message]);

      // Handle different response types
      if (response.data.quiz) {
        setQuiz(response.data.quiz);
      }
      if (response.data.studyPlan) {
        setStudyPlan(response.data.studyPlan);
      }

      setMessage('');
      setMessageType('chat');
    } catch (error) {
      console.error('Failed to send message:', error);
      setMessages(prev => [...prev, {
        role: 'assistant',
        content: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date()
      }]);
    } finally {
      setLoading(false);
    }
  };

  const toggleDocumentSelection = (docId) => {
    setSelectedDocs(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);

    for (const file of acceptedFiles) {
      const formData = new FormData();
      formData.append('document', file);
      formData.append('userId', '507f1f77bcf86cd799439011');

      try {
        const response = await api.post('/documents/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });

        const newDoc = {
          _id: response.data.documentId,
          fileName: file.name,
          processed: response.data.processed
        };

        setDocuments(prev => [...prev, newDoc]);
        setSelectedDocs(prev => [...prev, newDoc._id]);
      } catch (error) {
        console.error('Upload failed:', error);
      }
    }

    setUploading(false);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'application/vnd.ms-powerpoint': ['.ppt'],
      'application/vnd.openxmlformats-officedocument.presentationml.presentation': ['.pptx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const renderQuiz = () => {
    if (!quiz) return null;

    return (
      <div className="bg-gradient-to-r from-purple-50 to-indigo-50 border border-purple-200 rounded-2xl p-6 mb-6 shadow-sm">
        <div className="flex items-center gap-3 mb-6">
          <div className="w-10 h-10 bg-gradient-to-r from-purple-500 to-indigo-600 rounded-xl flex items-center justify-center">
            <Brain className="w-5 h-5 text-white" />
          </div>
          <div>
            <h3 className="text-lg font-bold text-slate-800">Generated Quiz</h3>
            <p className="text-sm text-slate-600">{quiz.length} questions</p>
          </div>
        </div>

        <div className="space-y-6">
          {quiz.map((q, index) => (
            <div key={index} className="bg-white rounded-xl p-5 border border-slate-200 shadow-sm">
              <p className="font-semibold text-slate-800 mb-4">
                <span className="inline-flex items-center justify-center w-6 h-6 bg-purple-100 text-purple-700 rounded-full text-sm font-bold mr-3">
                  {index + 1}
                </span>
                {q.question}
              </p>

              <div className="space-y-2 mb-4">
                {q.options.map((option, i) => (
                  <div key={i} className={`p-3 rounded-xl border transition-all ${
                    i === q.correct 
                      ? 'bg-green-50 border-green-200 text-green-800' 
                      : 'bg-slate-50 border-slate-200 text-slate-700'
                  }`}>
                    <span className="font-medium mr-2">{String.fromCharCode(65 + i)}.</span>
                    {option}
                    {i === q.correct && (
                      <span className="ml-2 text-green-600 font-medium">âœ“ Correct</span>
                    )}
                  </div>
                ))}
              </div>

              <div className="bg-blue-50 border border-blue-200 rounded-xl p-3">
                <p className="text-sm text-blue-800">
                  <span className="font-medium">ðŸ’¡ Explanation:</span> {q.explanation}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const renderStudyPlan = () => {
    if (!studyPlan) return null;

    return (
      <div className="bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-2xl p-6 mb-6 shadow-sm">
        <div className="flex items-center gap-3 mb-6">
          <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center">
            <Calendar className="w-5 h-5 text-white" />
          </div>
          <div>
            <h3 className="text-lg font-bold text-slate-800">{studyPlan.title}</h3>
            <p className="text-sm text-slate-600">Duration: {studyPlan.totalDuration}</p>
          </div>
        </div>

        <div className="space-y-4">
          {studyPlan.schedule.slice(0, 3).map((day, index) => (
            <div key={index} className="bg-white rounded-xl p-5 border border-slate-200 shadow-sm">
              <div className="flex items-center gap-3 mb-4">
                <div className="w-8 h-8 bg-green-100 text-green-700 rounded-lg flex items-center justify-center font-bold text-sm">
                  {day.day}
                </div>
                <div>
                  <h4 className="font-semibold text-slate-800">Day {day.day}</h4>
                  <p className="text-sm text-slate-600">{day.date}</p>
                </div>
              </div>

              <div className="space-y-3">
                {day.sessions.map((session, i) => (
                  <div key={i} className="bg-slate-50 rounded-lg p-4">
                    <p className="font-medium text-slate-800 mb-2">
                      <span className="text-green-600">{session.time}:</span> {session.topic}
                    </p>
                    <ul className="space-y-1">
                      {session.activities.map((activity, j) => (
                        <li key={j} className="text-sm text-slate-600 flex items-start gap-2">
                          <span className="text-green-500 mt-1">â€¢</span>
                          <span>{activity}</span>
                        </li>
                      ))}
                    </ul>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <div className="flex h-screen max-w-7xl mx-auto">
        {/* Sidebar */}
        <div className="w-80 bg-white/80 backdrop-blur-xl border-r border-slate-200/60 shadow-xl">
          <div className="p-6">
            {/* Header */}
            <div className="flex items-center gap-3 mb-6">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-xl flex items-center justify-center">
                <Sparkles className="w-5 h-5 text-white" />
              </div>
              <div>
                <h1 className="text-xl font-bold text-slate-800">AI Assistant</h1>
                <p className="text-sm text-slate-500">
                  {sessionId ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Connecting...'}
                </p>
              </div>
            </div>

            {/* Upload Section */}
            <div className="mb-8">
              <div className="flex items-center gap-2 mb-4">
                <BookOpen className="w-4 h-4 text-slate-600" />
                <h3 className="font-semibold text-slate-700">Documents</h3>
              </div>

              <div 
                {...getRootProps()} 
                className={`relative border-2 border-dashed rounded-2xl p-6 text-center cursor-pointer mb-4 transition-all duration-300 ${
                  isDragActive 
                    ? 'border-blue-400 bg-blue-50/50' 
                    : 'border-slate-300 hover:border-blue-300 hover:bg-slate-50/50'
                } ${uploading ? 'opacity-50 pointer-events-none' : ''}`}
              >
                <input {...getInputProps()} />
                <div className="flex flex-col items-center">
                  <div className={`w-12 h-12 rounded-xl flex items-center justify-center mb-3 transition-colors ${
                    isDragActive ? 'bg-blue-100' : 'bg-slate-100'
                  }`}>
                    <Upload className={`w-6 h-6 ${
                      isDragActive ? 'text-blue-600' : 'text-slate-500'
                    }`} />
                  </div>
                  {uploading ? (
                    <div className="flex items-center gap-2">
                      <div className="w-4 h-4 border-2 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
                      <p className="text-sm text-slate-600">Uploading...</p>
                    </div>
                  ) : (
                    <div>
                      <p className="text-sm font-medium text-slate-700 mb-1">
                        {isDragActive ? 'Drop files here' : 'Drop files or click to upload'}
                      </p>
                      <p className="text-xs text-slate-500">PDF, DOCX, PPT, TXT (max 10MB)</p>
                    </div>
                  )}
                </div>
              </div>

              {documents.length > 0 && (
                <div className="space-y-2 max-h-40 overflow-y-auto">
                  <p className="text-xs font-medium text-slate-600 mb-2">
                    {documents.length} document{documents.length !== 1 ? 's' : ''} uploaded
                  </p>
                  {documents.map((doc) => (
                    <label key={doc._id} className={`flex items-center gap-3 p-3 rounded-xl cursor-pointer transition-all ${
                      selectedDocs.includes(doc._id) 
                        ? 'bg-blue-50 border border-blue-200' 
                        : 'bg-slate-50 hover:bg-slate-100 border border-transparent'
                    }`}>
                      <input
                        type="checkbox"
                        checked={selectedDocs.includes(doc._id)}
                        onChange={() => toggleDocumentSelection(doc._id)}
                        className="w-4 h-4 text-blue-600 rounded focus:ring-blue-500"
                      />
                      <FileText className="w-4 h-4 text-slate-500 flex-shrink-0" />
                      <span className="text-sm text-slate-700 truncate">{doc.fileName}</span>
                    </label>
                  ))}
                </div>
              )}
            </div>

            {/* Quick Actions */}
            <div>
              <div className="flex items-center gap-2 mb-4">
                <MessageCircle className="w-4 h-4 text-slate-600" />
                <h3 className="font-semibold text-slate-700">Quick Actions</h3>
              </div>
              <div className="space-y-3">
                <button
                  onClick={() => {
                    setMessageType('chat');
                    setMessage('');
                  }}
                  className={`w-full p-4 text-left rounded-xl font-medium transition-all duration-200 ${
                    messageType === 'chat' 
                      ? 'bg-blue-50 border-2 border-blue-200 text-blue-700 shadow-sm' 
                      : 'bg-white hover:bg-slate-50 border-2 border-slate-200 text-slate-700 hover:border-slate-300'
                  }`}
                >
                  <div className="flex items-center gap-3">
                    <MessageCircle className="w-5 h-5" />
                    <span>Chat Mode</span>
                  </div>
                </button>

                <button
                  onClick={() => {
                    setMessageType('quiz');
                    setMessage('');
                  }}
                  className={`w-full p-4 text-left rounded-xl font-medium transition-all duration-200 ${
                    messageType === 'quiz' 
                      ? 'bg-purple-50 border-2 border-purple-200 text-purple-700 shadow-sm' 
                      : 'bg-white hover:bg-slate-50 border-2 border-slate-200 text-slate-700 hover:border-slate-300'
                  }`}
                >
                  <div className="flex items-center gap-3">
                    <Brain className="w-5 h-5" />
                    <span>Generate Quiz</span>
                  </div>
                </button>

                <button
                  onClick={() => {
                    setMessageType('study-plan');
                    setMessage('');
                  }}
                  className={`w-full p-4 text-left rounded-xl font-medium transition-all duration-200 ${
                    messageType === 'study-plan' 
                      ? 'bg-green-50 border-2 border-green-200 text-green-700 shadow-sm' 
                      : 'bg-white hover:bg-slate-50 border-2 border-slate-200 text-slate-700 hover:border-slate-300'
                  }`}
                >
                  <div className="flex items-center gap-3">
                    <Calendar className="w-5 h-5" />
                    <span>Study Plan</span>
                  </div>
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Main Chat Area */}
        <div className="flex-1 flex flex-col bg-white/60 backdrop-blur-sm">
          {/* Chat Header */}
          <div className="px-6 py-4 border-b border-slate-200/60 bg-white/80 backdrop-blur-xl">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-lg font-semibold text-slate-800">
                  {messageType === 'chat' ? 'Chat Assistant' :
                   messageType === 'quiz' ? 'Quiz Generator' :
                   'Study Plan Creator'}
                </h2>
                <p className="text-sm text-slate-500">
                  {selectedDocs.length > 0 
                    ? `${selectedDocs.length} document${selectedDocs.length !== 1 ? 's' : ''} selected`
                    : 'No documents selected'}
                </p>
              </div>
              <div className={`px-3 py-1 rounded-full text-xs font-medium ${
                messageType === 'chat' ? 'bg-blue-100 text-blue-700' :
                messageType === 'quiz' ? 'bg-purple-100 text-purple-700' :
                'bg-green-100 text-green-700'
              }`}>
                {messageType === 'chat' ? 'Chat' :
                 messageType === 'quiz' ? 'Quiz' : 'Plan'}
              </div>
            </div>
          </div>

          {/* Messages Area */}
          <div className="flex-1 overflow-y-auto p-6 space-y-6">
            {messages.length === 0 && (
              <div className="flex flex-col items-center justify-center h-full text-center">
                <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-2xl flex items-center justify-center mb-4">
                  <Sparkles className="w-8 h-8 text-white" />
                </div>
                <h3 className="text-xl font-semibold text-slate-800 mb-2">Welcome to AI Assistant</h3>
                <p className="text-slate-600 max-w-md">
                  {messageType === 'chat' ? 'Start a conversation by typing a message below.' :
                   messageType === 'quiz' ? 'Select documents and click send to generate a quiz.' :
                   'Select documents and click send to create a personalized study plan.'}
                </p>
              </div>
            )}

            {renderQuiz()}
            {renderStudyPlan()}

            {messages.map((msg, index) => (
              <div key={index} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`max-w-3xl px-6 py-4 rounded-2xl shadow-sm ${
                  msg.role === 'user'
                    ? 'bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-br-md'
                    : 'bg-white border border-slate-200 text-slate-800 rounded-bl-md'
                }`}>
                  <div className="whitespace-pre-wrap">{msg.content}</div>
                  {msg.sources && msg.sources.length > 0 && (
                    <div className="mt-3 pt-3 border-t border-white/20 text-xs opacity-75">
                      ðŸ“„ Sources: {msg.sources.length} document chunks
                    </div>
                  )}
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>

          {/* Input Area */}
          <div className="p-6 border-t border-slate-200/60 bg-white/80 backdrop-blur-xl">
            <div className="flex gap-4 items-end">
              <div className="flex-1">
                <textarea
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder={
                    messageType === 'chat' ? 'Type your message...' :
                    messageType === 'quiz' ? 'Click send to generate quiz from selected documents' :
                    'Click send to create study plan from selected documents'
                  }
                  className="w-full p-4 border border-slate-300 rounded-2xl resize-none focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all bg-white/90"
                  rows="2"
                  disabled={messageType !== 'chat'}
                />
              </div>

              <button
                onClick={() => setIsListening(!isListening)}
                className={`p-4 rounded-2xl transition-all duration-200 ${
                  isListening 
                    ? 'bg-red-500 hover:bg-red-600 text-white shadow-lg' 
                    : 'bg-slate-100 hover:bg-slate-200 text-slate-600'
                }`}
              >
                {isListening ? <MicOff size={20} /> : <Mic size={20} />}
              </button>

              <button
                onClick={handleSend}
                disabled={(messageType === 'chat' && !message.trim()) || loading}
                className={`p-4 rounded-2xl text-white font-medium transition-all duration-200 transform hover:scale-105 disabled:opacity-50 disabled:transform-none shadow-lg ${
                  messageType === 'quiz' 
                    ? 'bg-gradient-to-r from-purple-500 to-purple-600 hover:from-purple-600 hover:to-purple-700' :
                  messageType === 'study-plan' 
                    ? 'bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700' :
                    'bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700'
                }`}
              >
                {loading ? (
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                ) : (
                  messageType === 'quiz' ? <Brain size={20} /> :
                  messageType === 'study-plan' ? <Calendar size={20} /> :
                  <Send size={20} />
                )}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
