import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { ArrowLeft, Calendar, FileText, Clock, CheckCircle } from 'lucide-react';
import api from '../services/api';

const StudyPlanner = () => {
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [studyPlan, setStudyPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [duration, setDuration] = useState(7);

  useEffect(() => {
    loadDocuments();
  }, []);

  const loadDocuments = async () => {
    try {
      const response = await api.get('/documents');
      setDocuments(response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
    }
  };

  const toggleDocument = (docId) => {
    setSelectedDocs(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const generateStudyPlan = async () => {
    if (selectedDocs.length === 0) {
      alert('Please select at least one document');
      return;
    }

    setLoading(true);
    try {
      const response = await api.post('/chat/message', {
        message: 'Create a study plan',
        documentIds: selectedDocs,
        messageType: 'study-plan'
      });

      if (response.data.studyPlan) {
        setStudyPlan(response.data.studyPlan);
      }
    } catch (error) {
      console.error('Failed to generate study plan:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <header className="glass-effect shadow-xl border-b border-white/20 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center space-x-4">
              <Link to="/" className="p-2 text-slate-600 hover:text-slate-800 hover:bg-white/50 rounded-xl transition-all duration-200">
                <ArrowLeft size={20} />
              </Link>
              <div className="w-12 h-12 bg-gradient-to-r from-orange-500 to-amber-600 rounded-2xl flex items-center justify-center shadow-lg">
                <Calendar className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-orange-600 to-amber-600 bg-clip-text text-transparent">
                  Study Planner
                </h1>
                <p className="text-sm text-slate-500 font-medium">Create personalized study schedules</p>
              </div>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-8 px-6 lg:px-8">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Document Selection */}
          <div className="card">
            <div className="p-6">
              <h2 className="text-xl font-bold text-slate-800 mb-4">Select Documents</h2>
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {documents.map((doc) => (
                  <label key={doc._id} className={`flex items-center gap-3 p-4 rounded-xl cursor-pointer transition-all ${
                    selectedDocs.includes(doc._id) 
                      ? 'bg-gradient-to-r from-orange-50 to-amber-50 border-2 border-orange-300' 
                      : 'bg-white/70 hover:bg-white/90 border-2 border-transparent'
                  }`}>
                    <input
                      type="checkbox"
                      checked={selectedDocs.includes(doc._id)}
                      onChange={() => toggleDocument(doc._id)}
                      className="w-5 h-5 text-orange-600 rounded focus:ring-orange-500"
                    />
                    <FileText className="w-5 h-5 text-slate-500" />
                    <div className="flex-1">
                      <p className="font-medium text-slate-800">{doc.fileName}</p>
                      <p className="text-sm text-slate-500">Ready for planning</p>
                    </div>
                  </label>
                ))}
              </div>

              <div className="mt-6 space-y-4">
                <div>
                  <label className="block text-sm font-medium text-slate-700 mb-2">
                    Study Duration (days)
                  </label>
                  <select
                    value={duration}
                    onChange={(e) => setDuration(Number(e.target.value))}
                    className="w-full p-3 border border-slate-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-orange-500"
                  >
                    <option value={3}>3 days</option>
                    <option value={5}>5 days</option>
                    <option value={7}>7 days</option>
                    <option value={14}>14 days</option>
                    <option value={30}>30 days</option>
                  </select>
                </div>

                <button
                  onClick={generateStudyPlan}
                  disabled={loading || selectedDocs.length === 0}
                  className="w-full bg-gradient-to-r from-orange-500 to-amber-600 text-white font-bold py-3 px-6 rounded-xl hover:from-orange-600 hover:to-amber-700 disabled:opacity-50 transition-all"
                >
                  {loading ? 'Generating...' : 'Generate Study Plan'}
                </button>
              </div>
            </div>
          </div>

          {/* Study Plan Display */}
          <div className="card">
            <div className="p-6">
              <h2 className="text-xl font-bold text-slate-800 mb-4">Your Study Plan</h2>

              {studyPlan ? (
                <div className="space-y-4">
                  <div className="bg-orange-50 border border-orange-200 rounded-xl p-4">
                    <h3 className="font-bold text-orange-800">{studyPlan.title}</h3>
                    <p className="text-orange-600">Duration: {studyPlan.totalDuration}</p>
                    <p className="text-orange-600">Documents: {studyPlan.documentsIncluded}</p>
                  </div>

                  <div className="space-y-3 max-h-96 overflow-y-auto">
                    {studyPlan.schedule.map((day) => (
                      <div key={day.day} className="bg-white border border-slate-200 rounded-xl p-4">
                        <div className="flex items-center gap-3 mb-3">
                          <div className="w-8 h-8 bg-orange-100 text-orange-700 rounded-lg flex items-center justify-center font-bold text-sm">
                            {day.day}
                          </div>
                          <div>
                            <h4 className="font-semibold text-slate-800">Day {day.day}</h4>
                            <p className="text-sm text-slate-600">{day.date}</p>
                          </div>
                        </div>

                        {day.sessions.map((session, i) => (
                          <div key={i} className="bg-slate-50 rounded-lg p-3 mb-2">
                            <div className="flex items-center gap-2 mb-2">
                              <Clock size={16} className="text-slate-500" />
                              <span className="font-medium text-slate-700">{session.time}</span>
                            </div>
                            <p className="font-medium text-slate-800 mb-2">{session.topic}</p>
                            <ul className="space-y-1">
                              {session.activities.map((activity, j) => (
                                <li key={j} className="text-sm text-slate-600 flex items-start gap-2">
                                  <CheckCircle size={14} className="text-green-500 mt-0.5 flex-shrink-0" />
                                  <span>{activity}</span>
                                </li>
                              ))}
                            </ul>
                          </div>
                        ))}
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div className="text-center py-12">
                  <Calendar className="w-16 h-16 text-slate-300 mx-auto mb-4" />
                  <p className="text-slate-500">Select documents and generate your personalized study plan</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
};

export default StudyPlanner;
