import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { ArrowLeft, Calendar, FileText, Clock, CheckCircle, Upload, Plus, BookOpen, Target, Zap } from 'lucide-react';
import { useDropzone } from 'react-dropzone';
import api from '../services/api';

const StudyPlanner = () => {
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [studyPlan, setStudyPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [duration, setDuration] = useState(7);
  const [activeTab, setActiveTab] = useState('upload');

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  useEffect(() => {
    loadDocuments();
  }, []);

  const loadDocuments = async () => {
    try {
      const response = await api.get('/documents');
      setDocuments(response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
    }
  };

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);

    for (const file of acceptedFiles) {
      const formData = new FormData();
      formData.append('document', file);

      try {
        const response = await api.post('/documents/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });

        const newDoc = {
          _id: response.data.documentId,
          fileName: file.name,
          fileSize: file.size,
          processed: response.data.processed,
          createdAt: new Date().toISOString()
        };

        setDocuments(prev => [...prev, newDoc]);
        setSelectedDocs(prev => [...prev, newDoc._id]);
      } catch (error) {
        console.error('Upload failed:', error);
      }
    }

    setUploading(false);
    setTimeout(() => loadDocuments(), 1000);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const toggleDocument = (docId) => {
    setSelectedDocs(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const generateStudyPlan = async () => {
    if (selectedDocs.length === 0) {
      alert('Please select at least one document');
      return;
    }

    setLoading(true);
    try {
      // Create session first
      const sessionResponse = await api.post('/chat/session', {});
      const sessionId = sessionResponse.data.sessionId;

      // Send study plan message
      const response = await api.post('/chat/message', {
        sessionId,
        message: `Create a ${duration} day study plan`,
        documentIds: selectedDocs,
        messageType: 'study-plan'
      });

      console.log('Study plan response:', response.data);

      if (response.data.studyPlan) {
        setStudyPlan(response.data.studyPlan);
      } else {
        console.error('No study plan in response');
        alert('Failed to generate study plan. Please try again.');
      }
    } catch (error) {
      console.error('Failed to generate study plan:', error);
      alert('Error generating study plan: ' + (error.response?.data?.error || error.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <header className="glass-effect shadow-xl border-b border-white/20 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16 sm:h-20">
            <div className="flex items-center space-x-3 sm:space-x-4">
              <Link to="/" className="p-2 text-slate-600 hover:text-slate-800 hover:bg-white/50 rounded-xl transition-all duration-200">
                <ArrowLeft size={20} />
              </Link>
              <div className="w-10 h-10 sm:w-12 sm:h-12 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-2xl flex items-center justify-center shadow-lg">
                <BookOpen className="w-5 h-5 sm:w-6 sm:h-6 text-white" />
              </div>
              <div>
                <h1 className="text-xl sm:text-2xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
                  Study Planner
                </h1>
                <p className="text-xs sm:text-sm text-slate-500 font-medium">AI-powered personalized study schedules</p>
              </div>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-6xl mx-auto py-6 sm:py-8 px-4 sm:px-6 lg:px-8">
        {/* Tab Navigation */}
        <div className="flex justify-center mb-6 sm:mb-8">
          <div className="bg-white rounded-2xl p-1.5 shadow-lg border border-slate-200/60 backdrop-blur-sm">
            <button
              onClick={() => setActiveTab('upload')}
              className={`px-4 sm:px-6 py-2.5 sm:py-3 rounded-xl font-medium transition-all duration-300 flex items-center gap-2 ${
                activeTab === 'upload'
                  ? 'bg-gradient-to-r from-indigo-500 to-purple-600 text-white shadow-lg transform scale-105'
                  : 'text-slate-600 hover:text-slate-800 hover:bg-slate-50'
              }`}
            >
              <Upload size={16} />
              <span className="hidden sm:inline">Upload & Select</span>
              <span className="sm:hidden">Upload</span>
            </button>
            <button
              onClick={() => setActiveTab('plan')}
              className={`px-4 sm:px-6 py-2.5 sm:py-3 rounded-xl font-medium transition-all duration-300 flex items-center gap-2 ${
                activeTab === 'plan'
                  ? 'bg-gradient-to-r from-indigo-500 to-purple-600 text-white shadow-lg transform scale-105'
                  : 'text-slate-600 hover:text-slate-800 hover:bg-slate-50'
              }`}
            >
              <Target size={16} />
              <span className="hidden sm:inline">Generate Plan</span>
              <span className="sm:hidden">Plan</span>
            </button>
          </div>
        </div>

        {/* Upload & Select Tab */}
        {activeTab === 'upload' && (
          <div className="max-w-4xl mx-auto space-y-6">
            <div className="card mb-6 sm:mb-8">
              <div className="p-4 sm:p-6">
                <div className="text-center mb-6">
                  <div className="w-16 h-16 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg">
                    <Upload className="w-8 h-8 text-white" />
                  </div>
                  <h2 className="text-xl sm:text-2xl font-bold text-slate-800 mb-2">Upload Study Materials</h2>
                  <p className="text-slate-600">Add your documents to create a personalized study plan</p>
                </div>

                {/* Upload Area */}
                <div 
                  {...getRootProps()} 
                  className={`border-2 border-dashed rounded-2xl p-8 sm:p-12 text-center cursor-pointer transition-all duration-300 ${
                    uploading ? 'opacity-50 pointer-events-none' : ''
                  } ${
                    isDragActive ? 'border-indigo-400 bg-indigo-50 scale-105 shadow-lg' : 'border-slate-300 hover:border-indigo-400 hover:bg-indigo-50/50'
                  }`}
                >
                  <input {...getInputProps()} />
                  <div className="flex flex-col items-center">
                    <div className={`w-16 h-16 sm:w-20 sm:h-20 rounded-2xl flex items-center justify-center mb-4 sm:mb-6 transition-all duration-300 ${
                      isDragActive ? 'bg-indigo-500 scale-110' : 'bg-slate-100 hover:bg-indigo-100'
                    }`}>
                      <Upload className={`w-8 h-8 sm:w-10 sm:h-10 transition-colors ${
                        isDragActive ? 'text-white' : 'text-slate-500 hover:text-indigo-600'
                      }`} />
                    </div>

                    {uploading ? (
                      <div className="flex flex-col items-center">
                        <div className="w-6 h-6 sm:w-8 sm:h-8 border-4 border-indigo-500 border-t-transparent rounded-full animate-spin mb-3 sm:mb-4"></div>
                        <p className="text-lg sm:text-xl font-bold text-indigo-600 mb-2">Uploading...</p>
                        <p className="text-slate-600 text-sm sm:text-base">Processing your documents</p>
                      </div>
                    ) : isDragActive ? (
                      <div>
                        <p className="text-lg sm:text-xl font-bold text-indigo-600 mb-2">Drop files here!</p>
                        <p className="text-slate-600 text-sm sm:text-base">Release to upload your documents</p>
                      </div>
                    ) : (
                      <div>
                        <p className="text-lg sm:text-xl font-bold text-slate-800 mb-3">
                          Drag & drop documents or click to browse
                        </p>
                        <p className="text-slate-500 mb-4 text-sm sm:text-base">
                          PDF, DOCX, TXT  Max 10MB per file
                        </p>
                        <button className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-4 sm:px-6 py-2.5 sm:py-3 rounded-xl font-medium hover:from-indigo-600 hover:to-purple-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105">
                          <Plus className="w-4 h-4 sm:w-5 sm:h-5 inline mr-2" />
                          Choose Files
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>

            {/* Document Selection */}
            {documents.length > 0 && (
              <div className="card">
                <div className="p-4 sm:p-6">
                  <div className="text-center mb-6">
                    <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl flex items-center justify-center mx-auto mb-3 shadow-lg">
                      <CheckCircle className="w-6 h-6 text-white" />
                    </div>
                    <h2 className="text-xl sm:text-2xl font-bold text-slate-800 mb-2">Select Documents</h2>
                    <p className="text-slate-600">Choose which documents to include in your study plan</p>
                  </div>
                  <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4 mb-6">
                    {documents.map((doc) => (
                      <label key={doc._id} className={`flex items-center gap-3 sm:gap-4 p-3 sm:p-4 rounded-xl cursor-pointer transition-all duration-300 border-2 ${
                        selectedDocs.includes(doc._id) 
                          ? 'bg-gradient-to-r from-indigo-50 to-purple-50 border-indigo-300 shadow-lg scale-105' 
                          : 'bg-white hover:bg-indigo-50/50 border-slate-200 hover:border-indigo-200 hover:shadow-md'
                      }`}>
                        <input
                          type="checkbox"
                          checked={selectedDocs.includes(doc._id)}
                          onChange={() => toggleDocument(doc._id)}
                          className="w-5 h-5 sm:w-6 sm:h-6 text-indigo-600 rounded-lg focus:ring-indigo-500"
                        />
                        <FileText className="w-6 h-6 sm:w-8 sm:h-8 text-indigo-500" />
                        <div className="flex-1 min-w-0">
                          <p className="font-bold text-slate-800 truncate text-sm sm:text-base">{doc.fileName}</p>
                          <p className="text-xs sm:text-sm text-slate-500">Ready for study planning</p>
                        </div>
                        {selectedDocs.includes(doc._id) && (
                          <CheckCircle className="w-6 h-6 text-green-500" />
                        )}
                      </label>
                    ))}
                  </div>

                  {selectedDocs.length > 0 && (
                    <div className="text-center">
                      <button
                        onClick={() => setActiveTab('plan')}
                        className="bg-gradient-to-r from-green-500 to-emerald-600 text-white font-bold py-3 sm:py-4 px-6 sm:px-8 rounded-xl hover:from-green-600 hover:to-emerald-700 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105 text-sm sm:text-base"
                      >
                        <span className="flex items-center gap-2">
                          <Zap className="w-4 h-4 sm:w-5 sm:h-5" />
                          Continue ({selectedDocs.length} selected)
                        </span>
                      </button>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Study Plan Tab */}
        {activeTab === 'plan' && (
          <div className="max-w-4xl mx-auto">
            <div className="card">
              <div className="p-4 sm:p-6">
                <div className="text-center mb-6">
                  <div className="w-16 h-16 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-2xl flex items-center justify-center mx-auto mb-4 shadow-lg">
                    <Target className="w-8 h-8 text-white" />
                  </div>
                  <h2 className="text-xl sm:text-2xl font-bold text-slate-800 mb-2">Generate Study Plan</h2>
                  <p className="text-slate-600">Create an AI-powered personalized learning schedule</p>
                </div>

                {selectedDocs.length === 0 ? (
                  <div className="text-center py-8 sm:py-12">
                    <div className="w-20 h-20 bg-slate-100 rounded-2xl flex items-center justify-center mx-auto mb-4">
                      <FileText className="w-10 h-10 text-slate-400" />
                    </div>
                    <p className="text-slate-500 mb-4 text-sm sm:text-base">No documents selected</p>
                    <button
                      onClick={() => setActiveTab('upload')}
                      className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white px-4 sm:px-6 py-2.5 sm:py-3 rounded-xl font-medium transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105"
                    >
                      <span className="flex items-center gap-2">
                        <ArrowLeft className="w-4 h-4" />
                        Back to Upload
                      </span>
                    </button>
                  </div>
                ) : (
                  <div>
                    {/* Settings */}
                    <div className="bg-gradient-to-r from-slate-50 to-blue-50 rounded-xl p-4 sm:p-6 mb-6 border border-slate-200/60">
                      <h3 className="font-bold text-slate-800 mb-4 flex items-center gap-2">
                        <div className="w-6 h-6 bg-indigo-500 rounded-lg flex items-center justify-center">
                          <Calendar className="w-4 h-4 text-white" />
                        </div>
                        Study Plan Settings
                      </h3>
                      <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 sm:gap-6">
                        <div>
                          <label className="block text-sm font-medium text-slate-700 mb-3">
                            Selected Documents ({selectedDocs.length})
                          </label>
                          <div className="space-y-2 max-h-32 overflow-y-auto custom-scrollbar">
                            {documents.filter(doc => selectedDocs.includes(doc._id)).map(doc => (
                              <div key={doc._id} className="flex items-center gap-2 text-sm text-slate-600 bg-white rounded-lg p-2 border border-slate-200">
                                <CheckCircle size={14} className="text-green-500 flex-shrink-0" />
                                <span className="truncate">{doc.fileName}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                        <div>
                          <label className="block text-sm font-medium text-slate-700 mb-3">
                            Study Duration
                          </label>
                          <select
                            value={duration}
                            onChange={(e) => setDuration(Number(e.target.value))}
                            className="w-full p-3 border border-slate-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 bg-white transition-all duration-200 hover:border-indigo-300"
                          >
                            <option value={3}>3 days - Quick Review</option>
                            <option value={5}>5 days - Standard</option>
                            <option value={7}>7 days - Comprehensive</option>
                            <option value={14}>14 days - Detailed Study</option>
                            <option value={30}>30 days - In-depth Learning</option>
                          </select>
                        </div>
                      </div>

                      <div className="text-center mt-6">
                        <button
                          onClick={generateStudyPlan}
                          disabled={loading}
                          className="bg-gradient-to-r from-indigo-500 to-purple-600 text-white font-bold py-3 sm:py-4 px-6 sm:px-8 rounded-xl hover:from-indigo-600 hover:to-purple-700 disabled:opacity-50 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105 disabled:transform-none text-sm sm:text-base"
                        >
                          {loading ? (
                            <div className="flex items-center gap-2 sm:gap-3">
                              <div className="w-4 h-4 sm:w-5 sm:h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                              <span>Generating Plan...</span>
                            </div>
                          ) : (
                            <div className="flex items-center gap-2 sm:gap-3">
                              <Zap className="w-4 h-4 sm:w-5 sm:h-5" />
                              <span>Generate Study Plan</span>
                            </div>
                          )}
                        </button>
                      </div>
                    </div>

                    {/* Study Plan Display */}
                    {studyPlan && (
                      <div className="space-y-4 sm:space-y-6">
                        <div className="bg-gradient-to-r from-indigo-50 to-purple-50 border border-indigo-200 rounded-xl p-4 sm:p-6 shadow-lg">
                          <h3 className="text-lg sm:text-xl font-bold text-indigo-800 mb-3 flex items-center gap-2">
                            <div className="w-8 h-8 bg-indigo-500 rounded-lg flex items-center justify-center">
                              <BookOpen className="w-4 h-4 text-white" />
                            </div>
                            {studyPlan.title}
                          </h3>
                          <div className="grid grid-cols-1 sm:grid-cols-3 gap-3 sm:gap-4 text-indigo-700">
                            <div className="flex items-center gap-2 bg-white/60 rounded-lg p-2 sm:p-3">
                              <Calendar className="w-4 h-4" />
                              <span className="text-sm sm:text-base font-medium">Duration: {studyPlan.totalDuration}</span>
                            </div>
                            <div className="flex items-center gap-2 bg-white/60 rounded-lg p-2 sm:p-3">
                              <FileText className="w-4 h-4" />
                              <span className="text-sm sm:text-base font-medium">Documents: {studyPlan.documentsIncluded}</span>
                            </div>
                            <div className="flex items-center gap-2 bg-white/60 rounded-lg p-2 sm:p-3">
                              <Target className="w-4 h-4" />
                              <span className="text-sm sm:text-base font-medium">Topics: {studyPlan.totalTopics || 'Multiple'}</span>
                            </div>
                          </div>
                        </div>

                        <div className="space-y-3 sm:space-y-4">
                          {studyPlan.schedule.map((day) => (
                            <div key={day.day} className="bg-white border border-slate-200 rounded-xl p-4 sm:p-6 shadow-sm hover:shadow-md transition-all duration-300">
                              <div className="flex items-center gap-3 sm:gap-4 mb-4">
                                <div className="w-10 h-10 sm:w-12 sm:h-12 bg-gradient-to-r from-indigo-500 to-purple-600 text-white rounded-xl flex items-center justify-center font-bold text-sm sm:text-lg shadow-lg">
                                  {day.day}
                                </div>
                                <div className="flex-1">
                                  <h4 className="text-base sm:text-lg font-bold text-slate-800">Day {day.day}</h4>
                                  <p className="text-slate-600 text-sm sm:text-base">{day.date}</p>
                                  {day.dayTitle && <p className="text-xs sm:text-sm text-indigo-600 font-medium bg-indigo-50 px-2 py-1 rounded-lg inline-block mt-1">{day.dayTitle}</p>}
                                </div>
                              </div>

                              <div className="space-y-3">
                                {day.sessions.map((session, i) => (
                                  <div key={i} className="bg-gradient-to-r from-slate-50 to-blue-50 rounded-xl p-3 sm:p-4 border border-slate-200/60">
                                    <div className="flex items-center gap-2 sm:gap-3 mb-3">
                                      <div className="w-6 h-6 bg-indigo-500 rounded-lg flex items-center justify-center">
                                        <Clock size={14} className="text-white" />
                                      </div>
                                      <span className="font-bold text-slate-700 text-sm sm:text-base">{session.time}</span>
                                      {session.source && <span className="text-xs bg-indigo-100 text-indigo-700 px-2 py-1 rounded-full font-medium">{session.source}</span>}
                                    </div>
                                    <h5 className="font-bold text-slate-800 mb-3 text-sm sm:text-base">{session.topic}</h5>
                                    <ul className="grid grid-cols-1 lg:grid-cols-2 gap-2">
                                      {session.activities.map((activity, j) => (
                                        <li key={j} className="text-xs sm:text-sm text-slate-600 flex items-start gap-2 bg-white rounded-lg p-2">
                                          <CheckCircle size={14} className="text-green-500 mt-0.5 flex-shrink-0" />
                                          <span>{activity}</span>
                                        </li>
                                      ))}
                                    </ul>
                                  </div>
                                ))}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default StudyPlanner;
