import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [confirmationCode, setConfirmationCode] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { login, signup, confirmSignup } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (showConfirmation) {
        await confirmSignup(email, confirmationCode);
        setShowConfirmation(false);
        setIsLogin(true);
      } else if (isLogin) {
        await login(email, password);
        navigate('/');
      } else {
        await signup(email, password, phoneNumber);
        setShowConfirmation(true);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  if (showConfirmation) {
    return (
      <div className="min-h-screen flex items-center justify-center animate-fadeIn" style={{background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 50%, #e2e8f0 100%)'}}>
        <div className="card py-20 px-10 max-w-lg w-full mx-6 animate-slideIn">
          <div className="text-center mb-20">
            <div className="w-28 h-28 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full flex items-center justify-center mx-auto mb-10 shadow-2xl">
              <span className="text-white text-5xl font-bold">✓</span>
            </div>
            <h2 className="text-5xl font-bold bg-gradient-to-r from-green-600 to-emerald-600 bg-clip-text text-transparent mb-8">
              Verify Your Account
            </h2>
            <p className="text-slate-600 text-xl leading-relaxed">
              Enter the confirmation code sent to
            </p>
            <p className="text-slate-800 font-bold text-xl mt-2">{email}</p>
          </div>
          <form onSubmit={handleSubmit} className="space-y-16">
            <div>
              <input
                type="text"
                value={confirmationCode}
                onChange={(e) => setConfirmationCode(e.target.value)}
                placeholder="000000"
                className="w-full px-8 py-6 border-2 border-slate-200 rounded-3xl transition-all text-2xl text-center font-bold tracking-widest bg-white/90 backdrop-blur-sm focus:outline-none focus:ring-4 focus:ring-green-500/20 focus:border-green-400 shadow-lg"
                maxLength="6"
                required
              />
            </div>
            {error && (
              <div className="bg-red-50 border-2 border-red-200 text-red-700 px-6 py-4 rounded-2xl text-lg font-medium animate-slideIn">
                <div className="flex items-center space-x-3">
                  <div className="w-6 h-6 bg-red-500 rounded-full flex items-center justify-center flex-shrink-0">
                    <span className="text-white text-sm font-bold">!</span>
                  </div>
                  <span>{error}</span>
                </div>
              </div>
            )}
            <button
              type="submit"
              disabled={loading}
              className="btn-primary w-full py-6 text-xl font-bold disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
            >
              {loading ? (
                <div className="flex items-center justify-center space-x-3">
                  <div className="animate-spin rounded-full h-6 w-6 border-3 border-white border-t-transparent"></div>
                  <span>Verifying...</span>
                </div>
              ) : (
                'Verify Account'
              )}
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex animate-fadeIn" style={{background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 50%, #e2e8f0 100%)'}}>
      {/* Left Side - Branding */}
      <div className="hidden lg:flex lg:w-1/2 items-center justify-center p-16">
        <div className="text-center flex flex-col items-center animate-slideIn">
          <div className="w-32 h-32 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center mx-auto mb-12 shadow-2xl">
            <span className="text-6xl">🤖</span>
          </div>
          <h1 className="text-6xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent mb-8">
            AI Student Assistant
          </h1>
          <p className="text-2xl text-slate-600 mb-16 leading-relaxed max-w-lg">
            Your intelligent learning companion powered by advanced AI technology
          </p>
          <div className="space-y-8 text-left max-w-md">
            <div className="flex items-center space-x-4">
              <div className="w-4 h-4 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full"></div>
              <span className="text-xl text-slate-700 font-medium">Smart document analysis and Q&A</span>
            </div>
            <div className="flex items-center space-x-4">
              <div className="w-4 h-4 bg-gradient-to-r from-purple-500 to-purple-600 rounded-full"></div>
              <span className="text-xl text-slate-700 font-medium">Personalized study plans</span>
            </div>
            <div className="flex items-center space-x-4">
              <div className="w-4 h-4 bg-gradient-to-r from-green-500 to-emerald-600 rounded-full"></div>
              <span className="text-xl text-slate-700 font-medium">Interactive quiz generation</span>
            </div>
          </div>
        </div>
      </div>

      {/* Right Side - Login Form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-10">
        <div className="card py-20 px-10 w-full max-w-lg animate-slideIn" style={{animationDelay: '0.2s'}}>
          <div className="text-center mb-20">
            <div className="lg:hidden w-28 h-28 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-full flex items-center justify-center mx-auto mb-10 shadow-2xl">
              <span className="text-5xl">🤖</span>
            </div>
            <h2 className="text-5xl font-bold bg-gradient-to-r from-slate-800 to-slate-600 bg-clip-text text-transparent mb-8">
              {isLogin ? 'Welcome Back!' : 'Join Us Today'}
            </h2>
            <p className="text-slate-600 text-xl leading-relaxed">
              {isLogin ? 'Sign in to continue your AI-powered learning journey' : 'Create your account and start learning smarter with AI'}
            </p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-16">
            <div>
              <label className="block text-lg font-bold text-slate-800 mb-4">Email Address</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="your.email@example.com"
                className="w-full px-8 py-6 border-2 border-slate-200 rounded-3xl transition-all text-xl bg-white/90 backdrop-blur-sm focus:outline-none focus:ring-4 focus:ring-blue-500/20 focus:border-blue-400 shadow-lg"
                required
              />
            </div>

            <div>
              <label className="block text-lg font-bold text-slate-800 mb-4">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                className="w-full px-8 py-6 border-2 border-slate-200 rounded-3xl transition-all text-xl bg-white/90 backdrop-blur-sm focus:outline-none focus:ring-4 focus:ring-blue-500/20 focus:border-blue-400 shadow-lg"
                required
              />
            </div>

            {!isLogin && (
              <div>
                <label className="block text-lg font-bold text-slate-800 mb-4">Phone Number</label>
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="+1 (555) 123-4567"
                  className="w-full px-8 py-6 border-2 border-slate-200 rounded-3xl transition-all text-xl bg-white/90 backdrop-blur-sm focus:outline-none focus:ring-4 focus:ring-blue-500/20 focus:border-blue-400 shadow-lg"
                  required
                />
              </div>
            )}

            {error && (
              <div className="bg-red-50 border-2 border-red-200 text-red-700 px-6 py-4 rounded-2xl text-lg font-medium animate-slideIn">
                <div className="flex items-center space-x-3">
                  <div className="w-6 h-6 bg-red-500 rounded-full flex items-center justify-center flex-shrink-0">
                    <span className="text-white text-sm font-bold">!</span>
                  </div>
                  <span>{error}</span>
                </div>
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              className="btn-primary w-full py-6 text-xl font-bold disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
            >
              {loading ? (
                <div className="flex items-center justify-center space-x-3">
                  <div className="animate-spin rounded-full h-6 w-6 border-3 border-white border-t-transparent"></div>
                  <span>{isLogin ? 'Signing In...' : 'Creating Account...'}</span>
                </div>
              ) : (
                isLogin ? 'Sign In to Continue' : 'Create My Account'
              )}
            </button>
          </form>

          <div className="mt-20 text-center">
            <p className="text-slate-600 text-xl">
              {isLogin ? "Don't have an account? " : "Already have an account? "}
            </p>
            <button
              onClick={() => setIsLogin(!isLogin)}
              className="mt-4 text-xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent hover:from-blue-700 hover:to-indigo-700 transition-all duration-200 underline decoration-2 underline-offset-4"
            >
              {isLogin ? 'Create New Account' : 'Sign In Instead'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
