import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, FileText, Trash2 } from 'lucide-react';
import api from '../services/api';

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);
    setError(null);

    for (const file of acceptedFiles) {
      console.log('Uploading file:', file.name, file.size, file.type);

      const formData = new FormData();
      formData.append('document', file);
      formData.append('userId', '507f1f77bcf86cd799439011'); // Valid ObjectId format

      try {
        console.log('Making upload request...');
        const response = await api.post('/documents/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        console.log('Upload response:', response.data);
        const result = response.data;
        setDocuments(prev => [...prev, {
          id: result.documentId,
          name: file.name,
          size: file.size,
          processed: result.processed,
          uploadDate: new Date()
        }]);
      } catch (error) {
        console.error('Upload failed:', error);
        const errorMessage = error.response?.data?.error || error.message;
        setError(`Upload failed for ${file.name}: ${errorMessage}`);
      }
    }

    setUploading(false);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="min-h-screen" style={{background: 'linear-gradient(135deg, #e3f2fd 0%, #ffffff 100%)'}}>
      <div className="max-w-7xl mx-auto py-8 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold mb-8" style={{color: '#1976d2'}}>Document Management</h1>

          {error && (
            <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
              {error}
            </div>
          )}

          <div className="mb-8">
            <div 
              {...getRootProps()} 
              className={`border-2 border-dashed rounded-2xl p-8 text-center cursor-pointer transition-all ${uploading ? 'opacity-50' : ''}`}
              style={{
                borderColor: isDragActive ? '#2196f3' : '#e3f2fd',
                background: isDragActive ? '#e3f2fd' : 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)',
                boxShadow: '0 8px 24px rgba(33, 150, 243, 0.1)'
              }}
            >
              <input {...getInputProps()} />
              <Upload className="mx-auto h-16 w-16 mb-4" style={{color: isDragActive ? '#2196f3' : '#9e9e9e'}} />
              {uploading ? (
                <p className="text-xl font-medium" style={{color: '#2196f3'}}>Uploading...</p>
              ) : isDragActive ? (
                <p className="text-xl font-medium" style={{color: '#2196f3'}}>Drop the files here...</p>
              ) : (
                <div>
                  <p className="text-xl font-medium text-gray-700 mb-3">
                    Drag & drop documents here, or click to select
                  </p>
                  <p className="text-gray-500">
                    Supported: PDF, DOCX, TXT (max 10MB each)
                  </p>
                </div>
              )}
            </div>
          </div>

          <div className="rounded-2xl" style={{background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)', boxShadow: '0 8px 24px rgba(33, 150, 243, 0.12)', border: '1px solid #e3f2fd'}}>
            <div className="px-6 py-8 sm:p-8">
              <h3 className="text-xl font-bold text-gray-900 mb-6">
                Your Documents
              </h3>

              {documents.length === 0 ? (
                <p className="text-gray-500 text-center py-8">
                  No documents uploaded yet. Upload your first document to get started!
                </p>
              ) : (
                <div className="space-y-4">
                  {documents.map((doc) => (
                    <div key={doc.id} className="flex items-center justify-between p-4 rounded-xl transition-all hover:shadow-md" style={{background: 'rgba(255, 255, 255, 0.8)', border: '1px solid #e3f2fd'}}>
                      <div className="flex items-center space-x-4">
                        <FileText className="h-6 w-6" style={{color: '#2196f3'}} />
                        <div>
                          <p className="font-semibold text-gray-900">{doc.name}</p>
                          <p className="text-sm text-gray-600">
                            {formatFileSize(doc.size)} • {doc.processed ? '✅ Processed' : '⏳ Processing'}
                          </p>
                        </div>
                      </div>
                      <button className="p-2 rounded-lg transition-colors hover:bg-red-50" style={{color: '#f44336'}}>
                        <Trash2 className="h-5 w-5" />
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Documents;
