import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { ArrowLeft, Calendar, FileText, Clock, CheckCircle, Upload, Plus } from 'lucide-react';
import { useDropzone } from 'react-dropzone';
import api from '../services/api';

const StudyPlanner = () => {
  const [documents, setDocuments] = useState([]);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [studyPlan, setStudyPlan] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [duration, setDuration] = useState(7);
  const [activeTab, setActiveTab] = useState('upload');

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  useEffect(() => {
    loadDocuments();
  }, []);

  const loadDocuments = async () => {
    try {
      const response = await api.get('/documents');
      setDocuments(response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
    }
  };

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);

    for (const file of acceptedFiles) {
      const formData = new FormData();
      formData.append('document', file);

      try {
        const response = await api.post('/documents/upload', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });

        const newDoc = {
          _id: response.data.documentId,
          fileName: file.name,
          fileSize: file.size,
          processed: response.data.processed,
          createdAt: new Date().toISOString()
        };

        setDocuments(prev => [...prev, newDoc]);
        setSelectedDocs(prev => [...prev, newDoc._id]);
      } catch (error) {
        console.error('Upload failed:', error);
      }
    }

    setUploading(false);
    setTimeout(() => loadDocuments(), 1000);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const toggleDocument = (docId) => {
    setSelectedDocs(prev => 
      prev.includes(docId) 
        ? prev.filter(id => id !== docId)
        : [...prev, docId]
    );
  };

  const generateStudyPlan = async () => {
    if (selectedDocs.length === 0) {
      alert('Please select at least one document');
      return;
    }

    setLoading(true);
    try {
      const response = await api.post('/chat/message', {
        message: 'Create a study plan',
        documentIds: selectedDocs,
        messageType: 'study-plan'
      });

      if (response.data.studyPlan) {
        setStudyPlan(response.data.studyPlan);
        setActiveTab('plan');
      }
    } catch (error) {
      console.error('Failed to generate study plan:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-100">
      <header className="glass-effect shadow-xl border-b border-white/20 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 lg:px-8">
          <div className="flex items-center justify-between h-20">
            <div className="flex items-center space-x-4">
              <Link to="/" className="p-2 text-slate-600 hover:text-slate-800 hover:bg-white/50 rounded-xl transition-all duration-200">
                <ArrowLeft size={20} />
              </Link>
              <div className="w-12 h-12 bg-gradient-to-r from-orange-500 to-amber-600 rounded-2xl flex items-center justify-center shadow-lg">
                <Calendar className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-orange-600 to-amber-600 bg-clip-text text-transparent">
                  Study Planner
                </h1>
                <p className="text-sm text-slate-500 font-medium">Create personalized study schedules</p>
              </div>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-8 px-6 lg:px-8">
        {/* Tab Navigation */}
        <div className="flex justify-center mb-8">
          <div className="bg-white rounded-2xl p-2 shadow-lg border border-slate-200">
            <button
              onClick={() => setActiveTab('upload')}
              className={`px-6 py-3 rounded-xl font-medium transition-all ${
                activeTab === 'upload'
                  ? 'bg-gradient-to-r from-orange-500 to-amber-600 text-white shadow-lg'
                  : 'text-slate-600 hover:text-slate-800'
              }`}
            >
              ğŸ“ Upload & Select Documents
            </button>
            <button
              onClick={() => setActiveTab('plan')}
              className={`px-6 py-3 rounded-xl font-medium transition-all ${
                activeTab === 'plan'
                  ? 'bg-gradient-to-r from-orange-500 to-amber-600 text-white shadow-lg'
                  : 'text-slate-600 hover:text-slate-800'
              }`}
            >
              ğŸ“… Generate Study Plan
            </button>
          </div>
        </div>

        {/* Upload & Select Tab */}
        {activeTab === 'upload' && (
          <div className="max-w-4xl mx-auto">
            <div className="card mb-8">
              <div className="p-6">
                <h2 className="text-2xl font-bold text-slate-800 mb-6 text-center">ğŸ“ Upload Your Study Materials</h2>

                {/* Upload Area */}
                <div 
                  {...getRootProps()} 
                  className={`border-2 border-dashed rounded-2xl p-12 text-center cursor-pointer transition-all ${
                    uploading ? 'opacity-50 pointer-events-none' : ''
                  } ${
                    isDragActive ? 'border-orange-400 bg-orange-50 scale-105' : 'border-slate-300 hover:border-orange-400 hover:bg-orange-50/50'
                  }`}
                >
                  <input {...getInputProps()} />
                  <div className="flex flex-col items-center">
                    <div className={`w-20 h-20 rounded-2xl flex items-center justify-center mb-6 transition-all ${
                      isDragActive ? 'bg-orange-500 scale-110' : 'bg-slate-100 hover:bg-orange-100'
                    }`}>
                      <Upload className={`w-10 h-10 transition-colors ${
                        isDragActive ? 'text-white' : 'text-slate-500 hover:text-orange-600'
                      }`} />
                    </div>

                    {uploading ? (
                      <div className="flex flex-col items-center">
                        <div className="w-8 h-8 border-4 border-orange-500 border-t-transparent rounded-full animate-spin mb-4"></div>
                        <p className="text-xl font-bold text-orange-600 mb-2">Uploading...</p>
                        <p className="text-slate-600">Processing your documents</p>
                      </div>
                    ) : isDragActive ? (
                      <div>
                        <p className="text-xl font-bold text-orange-600 mb-2">Drop files here!</p>
                        <p className="text-slate-600">Release to upload your documents</p>
                      </div>
                    ) : (
                      <div>
                        <p className="text-xl font-bold text-slate-800 mb-3">
                          Drag & drop documents or click to browse
                        </p>
                        <p className="text-slate-500 mb-4">
                          PDF, DOCX, TXT â€¢ Max 10MB per file
                        </p>
                        <button className="bg-gradient-to-r from-orange-500 to-amber-600 text-white px-6 py-3 rounded-xl font-medium hover:from-orange-600 hover:to-amber-700 transition-all">
                          <Plus className="w-5 h-5 inline mr-2" />
                          Choose Files
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>

            {/* Document Selection */}
            {documents.length > 0 && (
              <div className="card">
                <div className="p-6">
                  <h2 className="text-2xl font-bold text-slate-800 mb-6 text-center">âœ… Select Documents for Study Plan</h2>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                    {documents.map((doc) => (
                      <label key={doc._id} className={`flex items-center gap-4 p-4 rounded-xl cursor-pointer transition-all border-2 ${
                        selectedDocs.includes(doc._id) 
                          ? 'bg-gradient-to-r from-orange-50 to-amber-50 border-orange-300 shadow-lg scale-105' 
                          : 'bg-white hover:bg-orange-50/50 border-slate-200 hover:border-orange-200'
                      }`}>
                        <input
                          type="checkbox"
                          checked={selectedDocs.includes(doc._id)}
                          onChange={() => toggleDocument(doc._id)}
                          className="w-6 h-6 text-orange-600 rounded-lg focus:ring-orange-500"
                        />
                        <FileText className="w-8 h-8 text-orange-500" />
                        <div className="flex-1">
                          <p className="font-bold text-slate-800">{doc.fileName}</p>
                          <p className="text-sm text-slate-500">Ready for study planning</p>
                        </div>
                        {selectedDocs.includes(doc._id) && (
                          <CheckCircle className="w-6 h-6 text-green-500" />
                        )}
                      </label>
                    ))}
                  </div>

                  {selectedDocs.length > 0 && (
                    <div className="text-center">
                      <button
                        onClick={() => setActiveTab('plan')}
                        className="bg-gradient-to-r from-green-500 to-emerald-600 text-white font-bold py-4 px-8 rounded-xl hover:from-green-600 hover:to-emerald-700 transition-all shadow-lg"
                      >
                        Continue to Generate Plan ({selectedDocs.length} documents selected)
                      </button>
                    </div>
                  )}
                </div>
              </div>
            )}
          </div>
        )}

        {/* Study Plan Tab */}
        {activeTab === 'plan' && (
          <div className="max-w-4xl mx-auto">
            <div className="card">
              <div className="p-6">
                <h2 className="text-2xl font-bold text-slate-800 mb-6 text-center">ğŸ“… Generate Your Study Plan</h2>

                {selectedDocs.length === 0 ? (
                  <div className="text-center py-12">
                    <FileText className="w-16 h-16 text-slate-300 mx-auto mb-4" />
                    <p className="text-slate-500 mb-4">No documents selected</p>
                    <button
                      onClick={() => setActiveTab('upload')}
                      className="bg-gradient-to-r from-orange-500 to-amber-600 text-white px-6 py-3 rounded-xl font-medium"
                    >
                      Go Back to Upload Documents
                    </button>
                  </div>
                ) : (
                  <div>
                    {/* Settings */}
                    <div className="bg-slate-50 rounded-xl p-6 mb-6">
                      <h3 className="font-bold text-slate-800 mb-4">Study Plan Settings</h3>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div>
                          <label className="block text-sm font-medium text-slate-700 mb-2">
                            Selected Documents ({selectedDocs.length})
                          </label>
                          <div className="space-y-2">
                            {documents.filter(doc => selectedDocs.includes(doc._id)).map(doc => (
                              <div key={doc._id} className="flex items-center gap-2 text-sm text-slate-600">
                                <CheckCircle size={16} className="text-green-500" />
                                <span>{doc.fileName}</span>
                              </div>
                            ))}
                          </div>
                        </div>
                        <div>
                          <label className="block text-sm font-medium text-slate-700 mb-2">
                            Study Duration
                          </label>
                          <select
                            value={duration}
                            onChange={(e) => setDuration(Number(e.target.value))}
                            className="w-full p-3 border border-slate-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-orange-500"
                          >
                            <option value={3}>3 days - Quick Review</option>
                            <option value={5}>5 days - Standard</option>
                            <option value={7}>7 days - Comprehensive</option>
                            <option value={14}>14 days - Detailed Study</option>
                            <option value={30}>30 days - In-depth Learning</option>
                          </select>
                        </div>
                      </div>

                      <div className="text-center mt-6">
                        <button
                          onClick={generateStudyPlan}
                          disabled={loading}
                          className="bg-gradient-to-r from-orange-500 to-amber-600 text-white font-bold py-4 px-8 rounded-xl hover:from-orange-600 hover:to-amber-700 disabled:opacity-50 transition-all shadow-lg"
                        >
                          {loading ? (
                            <div className="flex items-center gap-3">
                              <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                              <span>Generating Your Plan...</span>
                            </div>
                          ) : (
                            <div className="flex items-center gap-3">
                              <Calendar className="w-5 h-5" />
                              <span>Generate Study Plan</span>
                            </div>
                          )}
                        </button>
                      </div>
                    </div>

                    {/* Study Plan Display */}
                    {studyPlan && (
                      <div className="space-y-6">
                        <div className="bg-gradient-to-r from-orange-50 to-amber-50 border border-orange-200 rounded-xl p-6">
                          <h3 className="text-xl font-bold text-orange-800 mb-2">{studyPlan.title}</h3>
                          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-orange-700">
                            <div>ğŸ“… Duration: {studyPlan.totalDuration}</div>
                            <div>ğŸ“š Documents: {studyPlan.documentsIncluded}</div>
                            <div>ğŸ“ Topics: {studyPlan.totalTopics || 'Multiple'}</div>
                          </div>
                        </div>

                        <div className="space-y-4">
                          {studyPlan.schedule.map((day) => (
                            <div key={day.day} className="bg-white border border-slate-200 rounded-xl p-6 shadow-sm">
                              <div className="flex items-center gap-4 mb-4">
                                <div className="w-12 h-12 bg-gradient-to-r from-orange-500 to-amber-600 text-white rounded-xl flex items-center justify-center font-bold text-lg">
                                  {day.day}
                                </div>
                                <div>
                                  <h4 className="text-lg font-bold text-slate-800">Day {day.day}</h4>
                                  <p className="text-slate-600">{day.date}</p>
                                  {day.dayTitle && <p className="text-sm text-orange-600 font-medium">{day.dayTitle}</p>}
                                </div>
                              </div>

                              <div className="space-y-3">
                                {day.sessions.map((session, i) => (
                                  <div key={i} className="bg-slate-50 rounded-xl p-4">
                                    <div className="flex items-center gap-3 mb-3">
                                      <Clock size={18} className="text-slate-500" />
                                      <span className="font-bold text-slate-700">{session.time}</span>
                                      {session.source && <span className="text-xs bg-orange-100 text-orange-700 px-2 py-1 rounded-full">{session.source}</span>}
                                    </div>
                                    <h5 className="font-bold text-slate-800 mb-3">{session.topic}</h5>
                                    <ul className="grid grid-cols-1 md:grid-cols-2 gap-2">
                                      {session.activities.map((activity, j) => (
                                        <li key={j} className="text-sm text-slate-600 flex items-start gap-2">
                                          <CheckCircle size={16} className="text-green-500 mt-0.5 flex-shrink-0" />
                                          <span>{activity}</span>
                                        </li>
                                      ))}
                                    </ul>
                                  </div>
                                ))}
                              </div>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </main>
    </div>
