import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { Upload, FileText, Trash2 } from 'lucide-react';
import api from '../services/api';

const Documents = () => {
  const [documents, setDocuments] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  const onDrop = useCallback(async (acceptedFiles) => {
    setUploading(true);
    setError(null);

    for (const file of acceptedFiles) {
      console.log('Uploading file:', file.name, file.size, file.type);

      const formData = new FormData();
      formData.append('document', file);
      formData.append('userId', '507f1f77bcf86cd799439011'); // Valid ObjectId format

      try {
        console.log('Making upload request...');
        const response = await api.post('/documents/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });

        console.log('Upload response:', response.data);
        const result = response.data;
        setDocuments(prev => [...prev, {
          _id: result.documentId,
          fileName: file.name,
          fileSize: file.size,
          processed: result.processed,
          createdAt: new Date(),
          contentLength: result.contentLength || 0
        }]);

        if (result.processed) {
          setError(null);
        } else {
          setError(`Document uploaded but content extraction failed for ${file.name}. Please try a different file format.`);
        }
      } catch (error) {
        console.error('Upload failed:', error);
        const errorMessage = error.response?.data?.error || error.message;
        setError(`Upload failed for ${file.name}: ${errorMessage}`);
      }
    }

    setUploading(false);
  }, []);

  // Load existing documents on component mount
  React.useEffect(() => {
    loadDocuments();
  }, []);

  const loadDocuments = async () => {
    try {
      setLoading(true);
      const response = await api.get('/documents/507f1f77bcf86cd799439011');
      setDocuments(response.data);
      console.log('Loaded documents:', response.data);
    } catch (error) {
      console.error('Failed to load documents:', error);
      setError('Failed to load existing documents');
    } finally {
      setLoading(false);
    }
  };

  const deleteDocument = async (documentId) => {
    try {
      await api.delete(`/documents/${documentId}`);
      setDocuments(prev => prev.filter(doc => doc._id !== documentId));
    } catch (error) {
      console.error('Failed to delete document:', error);
      setError('Failed to delete document');
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxSize: 10 * 1024 * 1024
  });

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-2xl font-bold text-gray-900 mb-6">Document Management</h1>

          {error && (
            <div className="mb-4 p-4 bg-red-100 border border-red-400 text-red-700 rounded">
              {error}
            </div>
          )}

          <div className="mb-8">
            <div 
              {...getRootProps()} 
              className={`border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-gray-400 transition-colors cursor-pointer ${isDragActive ? 'border-blue-500 bg-blue-50' : ''} ${uploading ? 'opacity-50 cursor-not-allowed' : ''}`}
            >
              <input {...getInputProps()} />
              <Upload className="mx-auto h-12 w-12 text-gray-400 mb-4" />
              {uploading ? (
                <p className="text-lg text-gray-600">Uploading...</p>
              ) : isDragActive ? (
                <p className="text-lg text-blue-600">Drop the files here...</p>
              ) : (
                <div>
                  <p className="text-lg text-gray-600 mb-2">
                    Drag & drop documents here, or click to select
                  </p>
                  <p className="text-sm text-gray-500 mb-2">
                    Supported: PDF, DOCX, TXT (max 10MB each)
                  </p>
                  <p className="text-xs text-gray-400">
                    Documents will be processed for AI chat functionality
                  </p>
                </div>
              )}
            </div>
          </div>

          <div className="bg-white shadow rounded-lg">
            <div className="px-4 py-5 sm:p-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                Your Documents
              </h3>

              {loading ? (
                <p className="text-gray-500 text-center py-8">Loading documents...</p>
              ) : documents.length === 0 ? (
                <p className="text-gray-500 text-center py-8">
                  No documents uploaded yet. Upload your first document to get started!
                </p>
              ) : (
                <div className="space-y-3">
                  {documents.map((doc) => (
                    <div key={doc._id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex items-center space-x-3">
                        <FileText className="h-5 w-5 text-blue-500" />
                        <div>
                          <p className="font-medium text-gray-900">{doc.fileName}</p>
                          <p className="text-sm text-gray-500">
                            {formatFileSize(doc.fileSize)} • {doc.processed ? '✅ Processed' : '⏳ Processing'}
                            {doc.contentLength > 0 && ` • ${Math.ceil(doc.contentLength / 1000)}k chars`}
                          </p>
                          <p className="text-xs text-gray-400">
                            Uploaded: {new Date(doc.createdAt).toLocaleDateString()}
                          </p>
                        </div>
                      </div>
                      <button 
                        onClick={() => deleteDocument(doc._id)}
                        className="text-red-500 hover:text-red-700"
                        title="Delete document"
                      >
                        <Trash2 className="h-4 w-4" />
                      </button>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Documents;
