import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [confirmationCode, setConfirmationCode] = useState('');
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const { login, signup, confirmSignup } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (showConfirmation) {
        await confirmSignup(email, confirmationCode);
        setShowConfirmation(false);
        setIsLogin(true);
      } else if (isLogin) {
        await login(email, password);
        navigate('/');
      } else {
        await signup(email, password, phoneNumber);
        setShowConfirmation(true);
      }
    } catch (err) {
      setError(err.response?.data?.error || 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  if (showConfirmation) {
    return (
      <div className="min-h-screen flex items-center justify-center" style={{background: 'linear-gradient(135deg, #e3f2fd 0%, #ffffff 100%)'}}>
        <div className="p-12 rounded-3xl max-w-lg w-full mx-4" style={{background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)', border: '1px solid #e3f2fd', boxShadow: '0 20px 60px rgba(33, 150, 243, 0.2)'}}>
          <div className="text-center mb-12">
            <div className="w-24 h-24 rounded-full flex items-center justify-center mx-auto mb-8" style={{background: 'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)'}}>
              <span className="text-white text-4xl font-bold">✓</span>
            </div>
            <h2 className="text-4xl font-bold text-gray-900 mb-6">Verify Your Account</h2>
            <p className="text-gray-600 text-xl">
              Enter the confirmation code sent to <span className="font-semibold">{email}</span>
            </p>
          </div>
          <form onSubmit={handleSubmit} className="space-y-10">
            <div>
              <input
                type="text"
                value={confirmationCode}
                onChange={(e) => setConfirmationCode(e.target.value)}
                placeholder="Enter 6-digit code"
                className="w-full px-6 py-5 border rounded-2xl transition-all text-xl text-center"
                style={{borderColor: '#e3f2fd', background: 'rgba(255, 255, 255, 0.9)'}}
                onFocus={(e) => e.target.style.borderColor = '#2196f3'}
                onBlur={(e) => e.target.style.borderColor = '#e3f2fd'}
                maxLength="6"
                required
              />
            </div>
            {error && (
              <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg text-sm">
                {error}
              </div>
            )}
            <button
              type="submit"
              disabled={loading}
              className="w-full text-white font-bold py-6 px-8 rounded-2xl transition-all disabled:opacity-50 disabled:cursor-not-allowed text-xl"
              style={{background: 'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)', boxShadow: '0 8px 30px rgba(33, 150, 243, 0.5)'}}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-3px)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0)'}
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                  Verifying...
                </div>
              ) : (
                'Verify Account'
              )}
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex" style={{background: 'linear-gradient(135deg, #e3f2fd 0%, #ffffff 100%)'}}>
      {/* Left Side - Branding */}
      <div className="hidden lg:flex lg:w-1/2 items-center justify-center p-12">
        <div className="text-center flex flex-col items-center" style={{color: '#1976d2'}}>
          <div className="w-28 h-28 rounded-full flex items-center justify-center mx-auto mb-8" style={{background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)', boxShadow: '0 8px 24px rgba(33, 150, 243, 0.2)'}}>
            <span className="text-5xl">🤖</span>
          </div>
          <h1 className="text-5xl font-bold mb-6">AI Student Assistant</h1>
          <p className="text-xl opacity-90 mb-10">Your intelligent learning companion powered by advanced AI</p>
          <div className="space-y-6 text-center max-w-md">
            <div className="flex items-center justify-center">
              <div className="w-3 h-3 bg-current rounded-full mr-4"></div>
              <span className="text-lg">Smart document analysis and Q&A</span>
            </div>
            <div className="flex items-center justify-center">
              <div className="w-3 h-3 bg-current rounded-full mr-4"></div>
              <span className="text-lg">Personalized study plans</span>
            </div>
            <div className="flex items-center justify-center">
              <div className="w-3 h-3 bg-current rounded-full mr-4"></div>
              <span className="text-lg">Interactive quiz generation</span>
            </div>
          </div>
        </div>
      </div>

      {/* Right Side - Login Form */}
      <div className="w-full lg:w-1/2 flex items-center justify-center p-8">
        <div className="p-12 rounded-3xl w-full max-w-lg" style={{background: 'linear-gradient(135deg, #ffffff 0%, #f8fafc 100%)', boxShadow: '0 20px 60px rgba(33, 150, 243, 0.2)', border: '1px solid #e3f2fd'}}>
          <div className="text-center mb-12">
            <div className="lg:hidden w-24 h-24 rounded-full flex items-center justify-center mx-auto mb-8" style={{background: 'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)'}}>
              <span className="text-4xl">🤖</span>
            </div>
            <h2 className="text-5xl font-bold text-gray-900 mb-6">
              {isLogin ? 'Welcome Back' : 'Create Account'}
            </h2>
            <p className="text-gray-600 text-xl">
              {isLogin ? 'Sign in to continue your learning journey' : 'Join thousands of students using AI to learn better'}
            </p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-10">
            <div>
              <label className="block text-base font-semibold text-gray-700 mb-4">Email Address</label>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                className="w-full px-6 py-5 border rounded-2xl transition-all text-xl"
                style={{borderColor: '#e3f2fd', background: 'rgba(255, 255, 255, 0.9)'}}
                onFocus={(e) => e.target.style.borderColor = '#2196f3'}
                onBlur={(e) => e.target.style.borderColor = '#e3f2fd'}
                required
              />
            </div>

            <div>
              <label className="block text-base font-semibold text-gray-700 mb-4">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                className="w-full px-6 py-5 border rounded-2xl transition-all text-xl"
                style={{borderColor: '#e3f2fd', background: 'rgba(255, 255, 255, 0.9)'}}
                onFocus={(e) => e.target.style.borderColor = '#2196f3'}
                onBlur={(e) => e.target.style.borderColor = '#e3f2fd'}
                required
              />
            </div>

            {!isLogin && (
              <div>
                <label className="block text-base font-semibold text-gray-700 mb-4">Phone Number</label>
                <input
                  type="tel"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                  placeholder="+1 (555) 123-4567"
                  className="w-full px-6 py-5 border rounded-2xl transition-all text-xl"
                  style={{borderColor: '#e3f2fd', background: 'rgba(255, 255, 255, 0.9)'}}
                  onFocus={(e) => e.target.style.borderColor = '#2196f3'}
                  onBlur={(e) => e.target.style.borderColor = '#e3f2fd'}
                  required
                />
              </div>
            )}

            {error && (
              <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg text-sm">
                {error}
              </div>
            )}

            <button
              type="submit"
              disabled={loading}
              className="w-full text-white font-bold py-6 px-8 rounded-2xl transition-all disabled:opacity-50 disabled:cursor-not-allowed text-xl"
              style={{background: 'linear-gradient(135deg, #2196f3 0%, #1976d2 100%)', boxShadow: '0 8px 30px rgba(33, 150, 243, 0.5)'}}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-3px)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0)'}
            >
              {loading ? (
                <div className="flex items-center justify-center">
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                  {isLogin ? 'Signing In...' : 'Creating Account...'}
                </div>
              ) : (
                isLogin ? 'Sign In' : 'Create Account'
              )}
            </button>
          </form>

          <div className="mt-12 text-center">
            <p className="text-gray-600 text-xl">
              {isLogin ? "Don't have an account? " : "Already have an account? "}
              <button
                onClick={() => setIsLogin(!isLogin)}
                className="font-bold transition-colors text-xl"
                style={{color: '#2196f3'}}
                onMouseEnter={(e) => e.target.style.color = '#1976d2'}
                onMouseLeave={(e) => e.target.style.color = '#2196f3'}
              >
                {isLogin ? 'Sign Up' : 'Sign In'}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
